import React, { Component } from "react";
import axios from "axios";
//import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const server = "http://localhost:5000/api";

const Post = (props) => (
  <div className="post-box">
    <br></br>
    <div className="card w-75 mx-auto">
      <div className="card-body">
        <div class="media">
          <img src="..." class="mr-3" alt="..." />
          <div class="media-body">
            <h5 className="card-title p-3 mb-2 bg-primary text-white">
              {props.posts.name}
            </h5>
            Cras sit amet nibh libero, in gravida nulla. Nulla vel metus
            scelerisque ante sollicitudin. Cras purus odio, vestibulum in
            vulputate at, tempus viverra turpis. Fusce condimentum nunc ac nisi
            vulputate fringilla. Donec lacinia congue felis in faucibus.
          </div>
        </div>

        {/*  <h5 className="card-title p-3 mb-2 bg-primary text-white">
          {props.posts.name}
        </h5>
        <p className="text-left">{props.posts.post}</p>
        <p className="card-text">posted: {props.posts.dateCreated}</p> */}
      </div>
      <div>
        <p>reply btn</p>
      </div>
    </div>
  </div>
);

export default class Wall extends Component {
  constructor(props) {
    super(props);

    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePost = this.onChangePost.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      postList: [],
      name: "",
      post: "",
    };
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangePost(e) {
    this.setState({
      post: e.target.value,
    });
  }

  onSubmit(e) {
    e.preventDefault();

    const post = {
      name: this.state.name,
      post: this.state.post,
    };

    axios
      .post(`${server}/addPost`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      name: "",
      post: "",
    });

    axios
      .get(`${server}/get`)
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));
  }

  componentDidMount() {
    axios
      .get(`${server}/get`)
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));
  }

  postList() {
    console.log(this.state.postList);
    return this.state.postList.map((post) => {
      return <Post posts={post} />;
    });
  }

  render() {
    return (
      <div className="container-box">
        <div className="left-box">
          <p>ssfdd</p>
        </div>
        <div className="content-box">
          <div className="empty-box"></div>
          <div>
            <div className="add-post-box">
              <h3>Post anything here :)</h3>
              <form onSubmit={this.onSubmit}>
                <div className="form-group">
                  <label for="name-input"></label>
                  <input
                    placeholder="Name"
                    className="form-control"
                    type="text"
                    required
                    value={this.state.name}
                    onChange={this.onChangeName}
                    id="name-input"
                  />
                </div>
                <div className="form-group" id="comment">
                  <textarea
                    placeholder="What do you think?"
                    className="form-control"
                    id="exampleFormControlTextarea1"
                    rows="3"
                    required
                    value={this.state.post}
                    onChange={this.onChangePost}
                  />
                </div>
                <div>
                  <input
                    type="submit"
                    value="Send"
                    className="btn btn-primary"
                  />
                </div>
              </form>
            </div>
          </div>
          <div className="wall-post">
            <h3>Scratch on the Wall</h3>
            <div>{this.postList()}</div>
          </div>
        </div>
        <div className="right-box">
          <p>fdsafaga</p>
        </div>
      </div>
    );
  }
}
