import React, { Component, useState } from "react";
import axios from "axios";
import Modal from "react-bootstrap";
import Button from "react-bootstrap";
import Form from "react-bootstrap";

const server = "http://localhost:5000/api";

export default class EditPost extends React.Component {
  constructor(props) {
    super(props);

    this.onChangeUpdatePost = this.onChangeUpdatePost.bind(this);
    this.onSubmitUpdatePost = this.onSubmitUpdatePost.bind(this);
    this.editPostForm = this.editPostForm.bind(this);

    this.state = {
      updateTextPost: "",
    };
  }

  componentDidMount() {
    axios
      .get(`${server}/get/${this.props.postID}`)
      .then((post) => {
        this.setState({ updateTextPost: post.data.post });
      })
      .catch((err) => console.log(err));
  }

  onChangeUpdatePost(e) {
    this.setState({
      updateTextPost: e.target.value,
    });
  }

  onSubmitUpdatePost(e) {
    e.preventdefault();
    const post = {
      post: this.state.updateTextPost,
      withCredentials: true,
    };

    axios
      .post(`${server}/updatePost/${this.props.postID}`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      updateTextPost: "",
    });
  }

  editPostForm() {
    return (
      <Form onSubmit={() => this.onSubmitUpdatePost}>
        <Form.Group controlId="exampleForm.ControlTextarea1">
          <Form.Control
            as="textarea"
            rows={5}
            value={this.state.updateTextPost}
            onChange={this.onChangeUpdatePost}
          />
        </Form.Group>

        <div className="w-100 d-flex justify-content-center">
          <Button className="mx-auto" type="submit">
            Edit
          </Button>
        </div>
      </Form>
    );
  }

  EditPostModal = () => {
    const [show, setShow] = React.useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
      <>
        <button className="dropdown-item" onClick={handleShow}>
          Edit
        </button>

        <Modal
          size="lg"
          backdrop="static"
          keyboard={false}
          show={show}
          onHide={handleClose}
          centered
        >
          <Modal.Header closeButton>
            <div className="w-100 d-flex justify-content-center">
              <Modal.Title>Edit Post</Modal.Title>
            </div>
          </Modal.Header>
          <Modal.Body>{this.editPostForm()}</Modal.Body>
        </Modal>
      </>
    );
  };

  render() {
    return (
      <div>
        <this.EditPostModal />
      </div>
    );
  }
}
