import React, { Component } from "react";
import axios from "axios";
import AuthHandler from "./auth/Auth-Handler";
//import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const server = "http://localhost:5000/api";

export default class Wall extends Component {
  constructor(props) {
    super(props);

    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePost = this.onChangePost.bind(this);
    this.onSubmitPost = this.onSubmitPost.bind(this);

    this.onChangeReplyText = this.onChangeReplyText.bind(this);
    this.onSubmitReply = this.onSubmitReply.bind(this);

    this.state = {
      postList: [],
      name: "",
      post: "",
      replyList: [],

      myPostList: [],

      //login/register prop

      isLoggedIn: false,
      postAndReplyuserName: "",
    };
  }

  componentDidMount() {
    axios
      .get(`${server}/get`, { withCredentials: true })
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));

    axios
      .get(`${server}/getting/mypost`, { withCredentials: true })
      .then((res) => {
        const myPostList = res.data;
        this.setState({ myPostList });
      })
      .catch((err) => console.log(err));

    axios.get(`${server}/checkLogin`).then((res) => {
      this.setState.postAndReplyuserName = res.userName;

      if (res.data.userID === "Guest" || res.data.login === false) {
        this.setState({ isLoggedIn: false });
      } else {
        this.setState({ isLoggedIn: true });
      }
    });
  }

  ////////Add Post/////////
  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangePost(e) {
    this.setState({
      post: e.target.value,
    });
  }

  onSubmitPost(e) {
    e.preventDefault();

    const post = {
      name: this.state.name,
      post: this.state.post,
      withCredentials: true,
    };

    axios
      .post(`${server}/addPost`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      name: "",
      post: "",
    });

    axios
      .get(`${server}/get`)
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));
  }

  ////////Wall Post////////////
  Reply(props) {
    return (
      <div>
        <div>{props}</div>
      </div>
    );
  }

  Post(props) {
    return (
      <div className="post-box">
        <br></br>
        <div className="card w-75 mx-auto">
          <div className="card-body">
            <div className="media">
              <img src="..." className="mr-3" alt="..." />
              <div className="media-body">
                <div className="container">
                  <div className="row">
                    <div className="col-9">
                      <h5 className="card-title">{props.posts.userName}</h5>
                    </div>
                    <div className="col-sm align-items-start">
                      <div className="dropdown float-right">
                        <svg
                          className="btn btn-secondary dropdown-toggle float-right"
                          type="button"
                          id="dropdownMenuButton"
                          data-toggle="dropdown"
                          aria-haspopup="true"
                          aria-expanded="false"
                          width="1em"
                          height="1em"
                          viewBox="0 0 16 16"
                          className="bi bi-three-dots"
                          fill="currentColor"
                          xmlns="http://www.w3.org/2000/svg"
                        >
                          <path
                            fillRule="evenodd"
                            d="M3 9.5a1.5 1.5 0 1 1 0-3 1.5 1.5 0 0 1 0 3zm5 0a1.5 1.5 0 1 1 0-3 1.5 1.5 0 0 1 0 3zm5 0a1.5 1.5 0 1 1 0-3 1.5 1.5 0 0 1 0 3z"
                          />
                        </svg>

                        <div
                          className="dropdown-menu dropdown-menu-right"
                          aria-labelledby="dropdownMenuButton"
                        >
                          <a className="dropdown-item" href="#">
                            Edit
                          </a>
                          <a className="dropdown-item" href="#">
                            Delete
                          </a>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                <p className="text-left">{props.posts.post}</p>
                <p className="card-text">posted: {props.posts.dateCreated}</p>
              </div>
            </div>
          </div>
          <div className="container">
            <div className="row">
              <div className="col" id="#replyGroup">
                <div
                  type="button"
                  data-toggle="collapse"
                  data-target={`#id${props.posts._id}`}
                  aria-expanded="false"
                  aria-controls="comment-form"
                >
                  <svg
                    width="24"
                    height="24"
                    viewBox="0 0 1000 1000"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path d="M 850 88C 884 88 912 116 912 150C 912 150 912 650 912 650C 912 684 884 713 850 713C 850 713 506 713 506 713C 506 713 334 908 334 908C 331 912 325 914 321 912C 316 910 313 905 312 900C 312 900 312 713 312 713C 312 713 150 713 150 713C 116 713 88 684 88 650C 88 650 88 150 88 150C 88 116 115 88 149 88C 149 88 150 87 150 88C 150 88 850 88 850 88M 113 150C 113 150 113 650 113 650C 113 671 129 688 150 688C 150 688 325 688 325 688C 332 688 337 693 337 700C 337 700 337 867 337 867C 337 867 491 692 491 692C 493 689 496 688 500 688C 500 688 850 688 850 688C 871 688 887 671 887 650C 887 650 887 150 887 150C 887 129 871 113 850 113C 850 113 150 113 150 113C 129 113 113 129 113 150C 113 150 113 150 113 150M 360 365C 370 374 375 387 375 400C 375 413 370 426 360 435C 351 445 338 450 325 450C 312 450 299 445 290 435C 280 426 275 413 275 400C 275 387 280 374 290 365C 299 355 312 350 325 350C 338 350 351 355 360 365C 360 365 360 365 360 365M 535 365C 545 374 550 387 550 400C 550 413 545 426 535 435C 526 445 513 450 500 450C 487 450 474 445 465 435C 455 426 450 413 450 400C 450 387 455 374 465 365C 474 355 487 350 500 350C 513 350 526 355 535 365C 535 365 535 365 535 365M 710 365C 720 374 725 387 725 400C 725 413 720 426 710 435C 701 445 688 450 675 450C 662 450 649 445 640 435C 630 426 625 413 625 400C 625 387 630 374 640 365C 649 355 662 350 675 350C 688 350 701 355 710 365C 710 365 710 365 710 365" />
                  </svg>
                </div>

                <div
                  className="btn-group "
                  role="group"
                  aria-label="Basic example"
                >
                  <div>
                    <svg
                      width="24"
                      height="24"
                      viewBox="0 0 1000 1000"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path d="M 100 0C 45 0 0 45 0 100C 0 100 0 900 0 900C 0 955 45 1000 100 1000C 100 1000 900 1000 900 1000C 955 1000 1000 955 1000 900C 1000 900 1000 100 1000 100C 1000 45 955 0 900 0C 900 0 100 0 100 0C 100 0 100 0 100 0M 526 223C 526 223 526 223 526 223C 526 223 526 223 527 223C 527 223 727 423 727 423C 742 438 742 462 727 477C 712 492 688 492 673 477C 673 477 538 341 538 341C 538 341 538 750 538 750C 538 771 522 788 501 788C 480 789 463 771 463 750C 463 750 463 340 463 340C 463 340 327 477 327 477C 312 492 288 492 273 477C 258 462 258 438 273 423C 273 423 469 228 469 228C 476 218 488 212 500 212C 509 212 519 216 526 223C 526 223 526 223 526 223" />
                    </svg>
                  </div>
                  <div>
                    <svg
                      width="24"
                      height="24"
                      viewBox="0 0 1000 1000"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        d="M 100 0C 45 0 0 45 0 100C 0 100 0 900 0 900C 0 955 45 1000 100 1000C 100 1000 900 1000 900 1000C 955 1000 1000 955 1000 900C 1000 900 1000 100 1000 100C 1000 45 955 0 900 0C 900 0 100 0 100 0C 100 0 100 0 100 0M 100 75C 100 75 900 75 900 75C 914 75 925 86 925 100C 925 100 925 900 925 900C 925 914 914 925 900 925C 900 925 100 925 100 925C 86 925 75 914 75 900C 75 900 75 100 75 100C 75 86 86 75 100 75C 100 75 100 75 100 75M 526 223C 526 223 526 223 526 223C 526 223 526 223 527 223C 527 223 727 423 727 423C 742 438 742 462 727 477C 712 492 688 492 673 477C 673 477 538 341 538 341C 538 341 538 750 538 750C 538 771 522 788 501 788C 480 789 463 771 463 750C 463 750 463 340 463 340C 463 340 327 477 327 477C 312 492 288 492 273 477C 258 462 258 438 273 423C 273 423 469 228 469 228C 476 218 488 212 500 212C 509 212 519 216 526 223C 526 223 526 223 526 223"
                        transform="rotate(180,500,500)"
                      />
                    </svg>
                  </div>
                </div>
                <div className="collapse" id={`id${props.posts._id}`}>
                  <div>{props.replyTab}</div>
                  <div>
                    {props.posts.reply.map((replyObj) => {
                      return (
                        <div key={replyObj.postedDate}>
                          <div className="card-body">
                            <div className="media">
                              <img src="..." className="mr-3" alt="..." />
                              <div className="media-body">
                                <h6 className="card-title">
                                  {replyObj.userName} replied:
                                </h6>
                                <p className="text-left">
                                  {replyObj.replyText}
                                </p>
                                <p className="card-text">
                                  posted: {replyObj.postedDate}
                                </p>
                              </div>
                            </div>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className=""></div>
        </div>
      </div>
    );
  }

  postListDisp() {
    if (!this.state.postList || this.state.postList.length === 0) {
      return (
        <div className="mt-5">
          <p>This Wall is Empty</p>
          <p>Click 'Send Post' tab to post something</p>
        </div>
      );
    } else {
      return this.state.postList.map((post) => {
        return (
          <this.Post
            posts={post}
            key={post._id}
            replyTab={this.replyForm(post._id)}
          />
        );
      });
    }
  }

  myPostListDisp() {
    if (this.state.isLoggedIn) {
      if (!this.state.myPostList || this.state.myPostList.length === 0) {
        return (
          <div className="mt-5">
            <p>You haven't post anything</p>
            <p>Visit 'Send Post' tab to post something</p>
          </div>
        );
      } else {
        return this.state.myPostList.map((post) => {
          return (
            <this.Post
              posts={post}
              key={post._id}
              replyTab={this.replyForm(post._id)}
            />
          );
        });
      }
    } else {
      return (
        <div>
          <p>Oops you are log in as a guest, nothing to show here</p>
          <p>Log in or Sign Up to show your post here.</p>
        </div>
      );
    }
  }

  sendPost() {
    return (
      <div className="add-post-box">
        <h3>Post anything :)</h3>
        <form onSubmit={this.onSubmitPost}>
          <div className="form-group" id="comment">
            <textarea
              placeholder="What do you think?"
              className="form-control"
              id="exampleFormControlTextarea1"
              rows="3"
              required
              value={this.state.post}
              onChange={this.onChangePost}
            />
          </div>
          <div>
            <input type="submit" value="Send" className="btn btn-primary" />
          </div>
        </form>
      </div>
    );
  }

  /////////////////REPLY FORM//////////////////
  onChangeReplyText(e) {
    this.setState({
      replyText: e.target.value,
    });
  }

  onSubmitReply(post_id) {
    const reply = {
      userName: this.state.postAndReplyuserName,
      replyText: this.state.replyText,
    };

    axios
      .post(`${server}/replyPost/${post_id}`, reply)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  }

  replyForm(post_id) {
    return (
      <div>
        <form onSubmit={() => this.onSubmitReply(post_id)}>
          <div className="form-group" id="comment">
            <textarea
              placeholder="Write comment...."
              className="form-control"
              id="exampleFormControlTextarea1"
              rows="3"
              required
              onChange={this.onChangeReplyText}
            />
          </div>
          <div>
            <input
              type="submit"
              value="Send Reply"
              className="btn btn-primary btn-sm"
            />
          </div>
        </form>
      </div>
    );
  }

  render() {
    return (
      <div className="container-box">
        <div className="left-box">
          <p>ssfdd</p>
        </div>
        <div className="border content-box">
          <div className="empty-box"></div>

          <ul
            className="nav nav-tabs justify-content-center"
            id="myTab"
            role="tablist"
          >
            <li className="nav-item" role="presentation">
              <a
                className="nav-link active"
                id="home-tab"
                data-toggle="tab"
                href="#add-post"
                role="tab"
                aria-controls="home"
                aria-selected="true"
              >
                Send Post
              </a>
            </li>
            <li className="nav-item" role="presentation">
              <a
                className="nav-link"
                id="profile-tab"
                data-toggle="tab"
                href="#wall"
                role="tab"
                aria-controls="profile"
                aria-selected="false"
              >
                Wall
              </a>
            </li>
            <li className="nav-item" role="presentation">
              <a
                className="nav-link"
                id="contact-tab"
                data-toggle="tab"
                href="#mypost"
                role="tab"
                aria-controls="contact"
                aria-selected="false"
              >
                My Post
              </a>
            </li>
          </ul>
          <div className="tab-content" id="myTabContent">
            <div
              className="tab-pane fade show active"
              id="add-post"
              role="tabpanel"
              aria-labelledby="home-tab"
            >
              <div>{this.sendPost()}</div>
            </div>
            <div
              className="tab-pane fade"
              id="wall"
              role="tabpanel"
              aria-labelledby="profile-tab"
            >
              <div className="wall-post">
                <div>{this.postListDisp()}</div>
              </div>
            </div>
            <div
              className="tab-pane fade"
              id="mypost"
              role="tabpanel"
              aria-labelledby="contact-tab"
            >
              <div>
                <div className="mt-5">
                  <div>{this.myPostListDisp()}</div>
                </div>
                <div className="text-center"></div>
              </div>
            </div>
          </div>
        </div>
        <div className="right-box">
          <AuthHandler isLoggedIn={this.state.isLoggedIn} />
        </div>
      </div>
    );
  }
}
