import React from "react";
import axios from "axios";
import SearchMovie from "./Search-movie";

const server = "http://localhost:443/api";

export default class MovieDetails extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      movie: "",
    };
  }

  componentDidMount() {
    axios
      .get(`${server}/getmovie/${this.props.match.params.imdbID}`)
      .then((res) => {
        this.setState({ movie: res.data });
        console.log(res.data);
      })
      .catch((err) => console.log(err));
  }

  ratings = () => {
    const movie = this.state.movie;
    if (movie.Ratings) {
      return movie.Ratings.map((rating, index) => {
        return (
          <li key={index}>
            Source{rating.Source}: {rating.Value}
          </li>
        );
      });
    }
  };

  displayDetails = () => {
    const movie = this.state.movie;
    return (
      <div className="container">
        <h3>
          {movie.Title} ({movie.Year})
        </h3>
        <div className="row">
          <div>
            <img
              src={movie.Poster}
              height="300"
              width="220"
              alt={movie.Title}
            />

            <p>Genre: {movie.Genre}</p>
            <p>Director: {movie.Director}</p>
            <p>Writer : {movie.Writer}</p>
            <p>Actors : {movie.Actors}</p>
            <p>Language: {movie.Language}</p>
            <div>
              <ul>
                <li>
                  IMDB Rating : {movie.imdbRating}/10 From {movie.imdbVotes}{" "}
                  Votes
                </li>
                {this.ratings()}
              </ul>
            </div>
            <p>Rated : {movie.Rated}</p>
            <p>Year : {movie.Year}</p>
            <p>Runtime : {movie.Runtime}</p>
            <p>Type : {movie.Type}</p>
            <p>Plot: {movie.Plot}</p>
            <p>Awards: {movie.Awards}</p>
          </div>
        </div>
      </div>
    );
  };

  render() {
    return (
      <div>
        <SearchMovie />
        {this.displayDetails()}
      </div>
    );
  }
}
