import React, { Component } from "react";
import axios from "axios";
//import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const server = "http://localhost:5000/api";

const Post = (props) => (
  <div className="post-box">
    <br></br>
    <div className="card w-75 mx-auto">
      <div className="card-body">
        <div class="media">
          <img src="..." class="mr-3" alt="..." />
          <div class="media-body">
            <h5 className="card-title">{props.posts.name} said:</h5>
            <p className="text-left">{props.posts.post}</p>
            <p className="card-text">posted: {props.posts.dateCreated}</p>
          </div>
        </div>
      </div>
      <div>
        <p>reply btn</p>
      </div>
    </div>
  </div>
);

export default class Wall extends Component {
  constructor(props) {
    super(props);

    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePost = this.onChangePost.bind(this);
    this.onSubmitPost = this.onSubmitPost.bind(this);

    this.onChangeLoginEmail = this.onChangeLoginEmail.bind(this);
    this.onChangeLoginPassword = this.onChangeLoginPassword.bind(this);
    this.onSubmitLogin = this.onSubmitLogin.bind(this);

    this.state = {
      postList: [],
      name: "",
      post: "",

      //login prop
      email: "",
      password: "",
    };
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangePost(e) {
    this.setState({
      post: e.target.value,
    });
  }

  onChangeLoginEmail(e) {
    this.setState({
      email: e.target.value,
    });
  }

  onChangeLoginPassword(e) {
    this.setState({
      password: e.target.value,
    });
  }

  onSubmitPost(e) {
    e.preventDefault();

    const post = {
      name: this.state.name,
      post: this.state.post,
    };

    axios
      .post(`${server}/addPost`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      name: "",
      post: "",
    });

    axios
      .get(`${server}/get`)
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));
  }

  onSubmitLogin(e) {
    e.preventDefault();

    const loginProps = {
      email: this.state.email,
      password: this.state.password,
    };

    axios
      .post(`${server}/login`, loginProps)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    /* this.setState({
      loginEmail: "",
      loginPassword: "",
    }); */

    console.log(loginProps);
  }

  componentDidMount() {
    axios
      .get(`${server}/get`)
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));
  }

  postList() {
    console.log(this.state.postList);
    return this.state.postList.map((post) => {
      return <Post posts={post} />;
    });
  }

  loginForm() {
    return (
      <div class="card card-body mb-5">
        <div class="modal-header">
          <h5 class="modal-title" id="exampleModalLabel">
            Login
          </h5>
        </div>
        <div class="modal-body">
          <form onSubmit={this.onSubmitLogin}>
            <div class="form-group">
              <label for="login-email" class="col-form-label">
                Email
              </label>
              <input
                type="text"
                class="form-control"
                id="login-email"
                required
                value={this.state.loginEmail}
                onChange={this.onChangeLoginEmail}
              />
            </div>
            <div class="form-group">
              <label for="login-password" class="col-form-label">
                Password
              </label>
              <input
                type="text"
                class="form-control"
                id="login-password"
                required
                value={this.state.loginPassword}
                onChange={this.onChangeLoginPassword}
              />
            </div>

            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-secondary"
                data-dismiss="modal"
              >
                Close
              </button>
              <button type="submit" value="send" class="btn btn-primary">
                Login
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }

  registerForm() {
    return (
      <div class="card card-body mb-5">
        <div class="modal-header">
          <h5 class="modal-title" id="exampleModalLabel">
            Register
          </h5>
        </div>
        <div class="modal-body">
          <form>
            <div class="form-group">
              <label for="recipient-name" class="col-form-label">
                Username
              </label>
              <input type="text" class="form-control" id="recipient-name" />
            </div>
            <div class="form-group">
              <label for="recipient-name" class="col-form-label">
                Email
              </label>
              <input type="text" class="form-control" id="recipient-name" />
            </div>
            <div class="form-group">
              <label for="recipient-name" class="col-form-label">
                Password
              </label>
              <input type="text" class="form-control" id="recipient-name" />
            </div>
          </form>
        </div>
        <div class="modal-footer">
          <button type="button" class="btn btn-secondary" data-dismiss="modal">
            Close
          </button>
          <button type="button" class="btn btn-primary">
            Register
          </button>
        </div>
      </div>
    );
  }

  loginRegisterModalBox() {
    return (
      <div>
        <div class="modal-dialog">
          <div class="modal-content modal-dialog-centered" id="myGroup">
            <p class="justify-content-center mx-auto mt-1">
              <h5 className=" mt-4">Click Below to</h5>
              <div className="d-flex mt-4">
                <a
                  class="btn btn-primary"
                  data-toggle="collapse"
                  href="#login"
                  role="button"
                  aria-expanded="false"
                  aria-controls="login"
                >
                  Log In
                </a>
                <h5 className="ml-3 mr-3 mt-1"> Or </h5>
                <button
                  class="btn btn-primary"
                  type="button"
                  data-toggle="collapse"
                  data-target="#signup"
                  aria-expanded="false"
                  aria-controls="signup"
                >
                  Register
                </button>
              </div>
            </p>
            <div class="collapse" id="login" data-parent="#myGroup">
              {this.loginForm()}
            </div>
            <div class="collapse" id="signup" data-parent="#myGroup">
              {this.registerForm()}
            </div>
          </div>
        </div>
      </div>
    );
  }

  sendPost() {
    return (
      <div className="add-post-box">
        <h3>Post anything :)</h3>
        <form onSubmit={this.onSubmitPost}>
          <div className="form-group">
            <label for="name-input"></label>
            <input
              placeholder="Name"
              className="form-control"
              type="text"
              required
              value={this.state.name}
              onChange={this.onChangeName}
              id="name-input"
            />
          </div>
          <div className="form-group" id="comment">
            <textarea
              placeholder="What do you think?"
              className="form-control"
              id="exampleFormControlTextarea1"
              rows="3"
              required
              value={this.state.post}
              onChange={this.onChangePost}
            />
          </div>
          <div>
            <input type="submit" value="Send" className="btn btn-primary" />
          </div>
        </form>
      </div>
    );
  }

  render() {
    return (
      <div className="container-box">
        <div className="left-box">
          <p>ssfdd</p>
        </div>
        <div className="border content-box">
          <div className="empty-box"></div>

          <ul
            class="nav nav-tabs justify-content-center"
            id="myTab"
            role="tablist"
          >
            <li class="nav-item" role="presentation">
              <a
                class="nav-link active"
                id="home-tab"
                data-toggle="tab"
                href="#add-post"
                role="tab"
                aria-controls="home"
                aria-selected="true"
              >
                Send Post
              </a>
            </li>
            <li class="nav-item" role="presentation">
              <a
                class="nav-link"
                id="profile-tab"
                data-toggle="tab"
                href="#wall"
                role="tab"
                aria-controls="profile"
                aria-selected="false"
              >
                Wall
              </a>
            </li>
            <li class="nav-item" role="presentation">
              <a
                class="nav-link"
                id="contact-tab"
                data-toggle="tab"
                href="#mypost"
                role="tab"
                aria-controls="contact"
                aria-selected="false"
              >
                My Post
              </a>
            </li>
          </ul>
          <div class="tab-content" id="myTabContent">
            <div
              class="tab-pane fade show active"
              id="add-post"
              role="tabpanel"
              aria-labelledby="home-tab"
            >
              <div>{this.sendPost()}</div>
            </div>
            <div
              class="tab-pane fade"
              id="wall"
              role="tabpanel"
              aria-labelledby="profile-tab"
            >
              <div className="wall-post">
                <div>{this.postList()}</div>
              </div>
            </div>
            <div
              class="tab-pane fade"
              id="mypost"
              role="tabpanel"
              aria-labelledby="contact-tab"
            >
              <div>
                <p>Oops you are log in as a guest, nothing to show here</p>
                <p>Log in or Sign Up to show your post here.</p>

                <div>
                  <button
                    type="button"
                    class="btn btn-primary"
                    data-toggle="modal"
                    data-target="#exampleModal"
                    data-whatever="@getbootstrap"
                  >
                    Login/Register
                  </button>

                  <div
                    class="modal fade"
                    id="exampleModal"
                    tabindex="-1"
                    aria-labelledby="exampleModalLabel"
                    aria-hidden="true"
                  >
                    {this.loginRegisterModalBox()}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="right-box">
          <p>fdsafaga</p>
        </div>
      </div>
    );
  }
}
