import React from "react";
import axios from "axios";
import Movie from "./Movie";
import SearchMovie from "./Search-movie";

const server = "http://localhost:443/api";

export default class DisplayMovies extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      searchText: "",
      movieList: [],
      displayRecommendations: true,
      searchResult: [],
      searchKeyword: "",
    };
  }

  componentDidMount() {
    axios.get(`${server}/recommendations`).then((res) => {
      console.log(res.data);
      this.setState({ movieList: res.data });
      console.log(this.state.movieList);
    });
  }

  getSearchResult = (keyword, result) => {
    this.setState({ searchKeyword: keyword, searchResult: result });
    console.log(this.state.searchResult);
  };

  displaySearchResult = () => {
    if (!this.state.searchResult) {
      return (
        <div>
          <h5>Movie with Title "{this.state.searchKeyword}" is not found</h5>
        </div>
      );
    } else {
      return this.state.searchResult.map((movie, index) => {
        return (
          <div>
            {" "}
            <div className="card h-auto w-75 mx-auto " id="post-box">
              <div className="card-body mb-1 pb-0 border-bottom" id="post-cont">
                <div className="container h-auto pb-0 mx-0 px-0">
                  <div className="d-flex flex-row">
                    <h3 className="card-title h-auto">
                      {movie.Title} ({movie.Year})
                    </h3>
                  </div>
                </div>

                <div>
                  <p id="date" className="text-sm-left mb-1">
                    Type: {movie.Type}
                  </p>
                </div>
                <div className="media" id="post-box">
                  <img
                    src={movie.Poster}
                    height="180"
                    width="140"
                    className="mr-3 mb-2"
                    alt="..."
                  />
                </div>
              </div>
              >
            </div>
          </div>
        );
      });
    }
  };

  movieRecommendations = () => {
    return this.state.movieList.map((movie, index) => {
      return (
        <div key={index}>
          <Movie movie={movie} />
        </div>
      );
    });
  };

  hideRecommendations = () => {
    this.setState({ displayRecommendations: false });
  };

  displayRecommendations = () => {
    if (this.state.displayRecommendations) {
      console.log(this.state.displayRecommendations);
      return (
        <div>
          <h2 align="center">Movie Recommendations</h2>
          <br></br>
          <this.movieRecommendations />
        </div>
      );
    } else {
      return null;
    }
  };

  render() {
    return (
      <div>
        <SearchMovie
          server={server}
          getSearchResult={this.getSearchResult}
          hideRecommendations={this.hideRecommendations}
        />
        <br></br>
        <this.displayRecommendations />
        <this.displaySearchResult />
      </div>
    );
  }
}
