import React, { Component } from "react";
import axios from "axios";
//import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "mdbootstrap/css/mdb.min.css";

const server = "http://localhost:5000/api";

const Post = (props) => (
  <tr>
    <td>{props.posts.name}</td>
    <td>{props.posts.post}</td>
    <td>{props.posts.lastEdited}</td>
  </tr>
);

export default class Wall extends Component {
  constructor(props) {
    super(props);

    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePost = this.onChangePost.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      postList: [],
      name: "",
      post: "",
    };
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangePost(e) {
    this.setState({
      post: e.target.value,
    });
  }

  onSubmit(e) {
    e.preventDefault();

    const post = {
      name: this.state.name,
      post: this.state.post,
    };

    axios
      .post(`${server}/addPost`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      name: "",
      post: "",
    });

    axios
      .get(`${server}/get`)
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));
  }

  componentDidMount() {
    axios
      .get(`${server}/get`)
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));
  }

  postList() {
    console.log(this.state.postList);
    return this.state.postList.map((post) => {
      return <Post posts={post} />;
    });
  }

  render() {
    return (
      <div className="container-box">
        <div className="left-box">
          <p>ssfdd</p>
        </div>
        <div className="content-box">
          <div className="empty-box"></div>
          <div className="add-post-box">
            <h3>Post anything here :)</h3>

            <form onSubmit={this.onSubmit}>
              <nav class="navbar navbar-light amber lighten-4 mb-4">
                <a class="navbar-brand" href="#">
                  Navbar
                </a>

                <button
                  class="navbar-toggler first-button"
                  type="button"
                  data-toggle="collapse"
                  data-target="#navbarSupportedContent20"
                  aria-controls="navbarSupportedContent20"
                  aria-expanded="false"
                  aria-label="Toggle navigation"
                >
                  <div class="animated-icon1">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </button>

                <div
                  class="collapse navbar-collapse"
                  id="navbarSupportedContent20"
                >
                  <ul class="navbar-nav mr-auto">
                    <li class="nav-item active">
                      <a class="nav-link" href="#">
                        Home <span class="sr-only">(current)</span>
                      </a>
                    </li>
                    <li class="nav-item">
                      <a class="nav-link" href="#">
                        Features
                      </a>
                    </li>
                    <li class="nav-item">
                      <a class="nav-link" href="#">
                        Pricing
                      </a>
                    </li>
                  </ul>
                </div>
              </nav>

              <div className="form-group">
                <label for="name-input"></label>
                <input
                  placeholder="Name"
                  className="form-control"
                  type="text"
                  required
                  value={this.state.name}
                  onChange={this.onChangeName}
                  id="name-input"
                />
              </div>
              <div className="form-group" id="comment">
                <textarea
                  placeholder="What do you think?"
                  className="form-control"
                  id="exampleFormControlTextarea1"
                  rows="3"
                  required
                  value={this.state.post}
                  onChange={this.onChangePost}
                />
              </div>
              <div>
                <input type="submit" value="Send" className="btn btn-primary" />
              </div>
            </form>
          </div>
          <div className="wall-post">
            <h3>Scratch on the Wall</h3>
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Post</th>
                  <th>Date Modified</th>
                </tr>
              </thead>
              <tbody>{this.postList()}</tbody>
            </table>
          </div>
        </div>
        <div className="right-box">
          <p>fdsafaga</p>
        </div>
      </div>
    );
  }
}
