import React, { Component } from "react";
import axios from "axios";
import { Modal, Button, Form } from "react-bootstrap";

const server = "http://localhost:5000/api";

export default class EditPost extends Component {
  constructor(props) {
    super(props);

    this.onChangeUpdatePost = this.onChangeUpdatePost.bind(this);
    this.onSubmitUpdatePost = this.onSubmitUpdatePost.bind(this);

    this.state = {
      updateTextPost: "",
    };
  }

  componentDidMount() {
    axios
      .get(`${server}/get/${this.props.match.params.postID}`)
      .then((post) => {
        this.setState({ updateTextPost: post.data.post });
        console.log(this.state.updateTextPost);
      })
      .catch((err) => console.log(err));
  }

  onChangeUpdatePost(e) {
    console.log(this.state.updateTextPost);
    this.setState({
      updateTextPost: e.target.value,
    });
  }

  onSubmitUpdatePost() {
    const post = {
      post: this.state.updateTextPost,
      withCredentials: true,
    };

    axios
      .post(`${server}/updatePost/${this.props.match.params.postID}`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      updateTextPost: "",
    });
  }

  modal() {
    return (
      <div>
        <button
          type="button"
          class="btn btn-primary"
          data-toggle="modal"
          data-target="#exampleModal"
        >
          Launch demo modal
        </button>

        <div
          class="modal fade"
          id="exampleModal"
          tabindex="-1"
          role="dialog"
          aria-labelledby="exampleModalLabel"
          aria-hidden="true"
        >
          <div class="modal-dialog" role="document">
            <div class="modal-content">
              <div class="modal-header">
                <h5 class="modal-title" id="exampleModalLabel">
                  Modal title
                </h5>
                <button
                  type="button"
                  class="close"
                  data-dismiss="modal"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div class="modal-body"></div>
              <div class="modal-footer">
                <button
                  type="button"
                  class="btn btn-secondary"
                  data-dismiss="modal"
                >
                  Close
                </button>
                <button type="button" class="btn btn-primary">
                  Save changes
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  render() {
    return (
      <div>
        <this.modal />
      </div>
    );
  }
}
