import React, { Component } from "react";
import axios from "axios";
//import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const server = "http://localhost:5000/api";

const Post = (props) => (
  <tr>
    <td>{props.posts.name}</td>
    <td>{props.posts.post}</td>
    <td>{props.posts.lastEdited}</td>
  </tr>
);

export default class Wall extends Component {
  constructor(props) {
    super(props);

    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePost = this.onChangePost.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      name: "",
      post: "",
      postList: [],
    };
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangePost(e) {
    this.setState({
      post: e.target.value,
    });
  }

  onSubmit(e) {
    e.preventDefault();

    const post = {
      name: this.state.name,
      post: this.state.post,
    };

    axios
      .post(`${server}/addPost`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  }

  async componentDidMount() {
    try {
      axios.get(`${server}/get`).then((res) => {
        this.setState({ postList: res.data });
      });
    } catch (error) {
      console.log(error);
    }
  }

  postList() {
    console.log(this.state.postList);
    return this.states.postList.map((post) => {
      return <Post posts={post} />;
    });
  }

  render() {
    return (
      <div>
        <div>
          <h3>Post anything here :)</h3>
          <form onSubmit={this.onSubmit}>
            <div>
              <label>Name</label>
              <input
                type="text"
                required
                value={this.state.name}
                onChange={this.onChangeName}
              />
            </div>
            <div>
              <label>Post</label>
              <input
                type="text"
                required
                value={this.state.post}
                onChange={this.onChangePost}
              />
            </div>
            <div>
              <input type="submit" value="Send" />
            </div>
          </form>
        </div>
        <div>
          <h3>Scratch on the Wall</h3>
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Post</th>
                <th>Date Modified</th>
              </tr>
            </thead>
          </table>
        </div>
      </div>
    );
  }
}
