import React from "react";

export default class Movie extends React.Component {
  constructor(props) {
    super(props);
  }

  movieCards = () => {
    const movie = this.props.movie;
    return (
      <div className="card w-75 mx-auto" id="post-box">
        <div className="card-body mb-1 pb-1 border-bottom" id="post-cont">
          <div className="container h-auto pb-0 mx-0 px-0">
            <div className="d-flex flex-row">
              <h3 className="card-title h-auto">
                {movie.Title} ({movie.Year})
              </h3>
            </div>
          </div>

          <div>
            <p id="date" className="text-sm-left mb-1">
              Genre: {movie.Genre}, Rated: {movie.Rated}, Runtime:
              {movie.Runtime}
            </p>
          </div>
          <div className="media" id="post-box">
            <img
              src={movie.Poster}
              height="140"
              width="140"
              className="mr-3"
              alt="..."
            />
            <div className="media-body">
              <p className="text-sm-left mb-1">
                IMDB: {movie.imdbRating}/{movie.imdbVotes} votes
              </p>
              <p className="text-left post-content p-3">{movie.Plot}</p>
              <div className="container mb-3">
                <div className="row" id="replyGroup">
                  {/* {this.CommentTriggerButton()}
                  <UpvoteDownvote
                    post={this.props.posts}
                    server={this.props.server}
                  /> */}
                </div>
              </div>
            </div>
          </div>
        </div>
        <div
          className="collapse px-5  mt-2" /* id={`id${this.props.posts._id}`} */
        >
          {/* {this.CommentPostSection()} */}
        </div>
        <div className=""></div>
      </div>
    );
  };
  render() {
    return (
      <div>
        <this.movieCards />
      </div>
    );
  }
}
