import React from "react";
import axios from "axios";
import { Form, Button } from "react-bootstrap";
import { Container, Row, Col } from "react-bootstrap";

export default class SearchMovie extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      searchText: "",
      movieResult: [],
    };
  }

  onChangeSearchText = (e) => {
    this.setState({ searchText: e.target.value });
  };

  onClickSearch = (e) => {
    e.preventDefault();

    axios
      .get(`${this.props.server}/searchmovie/${this.state.searchText}`)
      .then((res) => {
        this.setState({ movieResult: res.data.Search });
        console.log(this.state.movieResult);
      })
      .catch((err) => console.log(err));

    this.props.hideRecommendations();
  };

  searchForm = () => {
    return (
      <div className="d-flex justify-content-center">
        <Form onSubmit={(e) => this.onClickSearch(e)}>
          <Form.Row className="align-items-center mt-2" xs="auto">
            <Col xs="auto">
              <Form.Label htmlFor="inlineFormInput" srOnly>
                Name
              </Form.Label>
              <Form.Control
                className="mb-2"
                id="inlineFormInput"
                placeholder="Movie Title"
                value={this.state.searchText}
                onChange={this.onChangeSearchText}
                required
              />
            </Col>
            <Col xs="auto">
              <Button type="submit" className="mb-2">
                Search
              </Button>
            </Col>
          </Form.Row>
        </Form>
      </div>
    );
  };

  render() {
    return <this.searchForm />;
  }
}
