import React from "react";
import axios from "axios";

export default class ReplyForm extends React.Component {
  constructor(props) {
    super(props);
    this.onChangeReplyText = this.onChangeReplyText.bind(this);
    this.onSubmitReply = this.onSubmitReply.bind(this);

    this.state = {
      replyText: "",
    };
  }

  onChangeReplyText(e) {
    this.setState({
      replyText: e.target.value,
    });
  }

  onSubmitReply(e, post_id) {
    e.preventDefault();

    const postIndex = this.state.postList.findIndex((el) => {
      return el._id === post_id;
    });

    const reply = {
      userName: this.state.userName,
      replyText: this.state.replyText,
    };

    axios
      .post(`${server}/replyPost/${post_id}`, reply)
      .then((res) => {
        const postList = this.state.postList;
        postList[postIndex].reply = res.data;
        console.log(postList[postIndex].reply);
        this.setState({ postList: postList });
      })
      .catch((err) => console.log(err));

    //console.log(this.state.postList);

    this.setState({
      replyText: "",
    });
  }

  replyForm(post_id) {
    return (
      <div>
        <form onSubmit={(e) => this.onSubmitReply(e, post_id)}>
          <div className="form-group" id="comment">
            <textarea
              placeholder="Write comment...."
              className="form-control"
              id="exampleFormControlTextarea1"
              rows="2"
              required
              onChange={this.onChangeReplyText}
            />
          </div>
          <div>
            <input
              type="submit"
              value="Send Reply"
              className="btn btn-primary btn-sm"
            />
          </div>
        </form>
      </div>
    );
  }

  render() {
    return (
      <div>
        <this.replyForm />
      </div>
    );
  }
}
