import React, { Component } from "react";
import axios from "axios";

export default class SendPost extends Component {
  constructor(props) {
    super(props);
    this.onChangePost = this.onChangePost.bind(this);
    this.onSubmitPost = this.onSubmitPost.bind(this);
    this.state = {
      post: "",
    };
  }

  onChangePost(e) {
    this.setState({
      post: e.target.value,
    });
  }

  onSubmitPost(e) {
    e.preventDefault();
    const post = {
      post: this.state.post,
      withCredentials: true,
    };

    console.log(this.props.server);

    axios
      .post(`${this.props.server}/addPost`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.state = {
      post: "",
    };

    axios
      .get(`${this.props.server}/get`)
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));
  }

  sendPost = () => {
    return (
      <div className="add-post-box">
        <h3>Post anything :)</h3>
        <form onSubmit={(e) => this.onSubmitPost(e)}>
          <div className="form-group" id="comment">
            <textarea
              placeholder="What do you think?"
              className="form-control"
              id="exampleFormControlTextarea1"
              rows="6"
              required
              value={this.state.post}
              onChange={this.onChangePost}
            />
          </div>
          <div>
            <input type="submit" value="Send" className="btn btn-primary" />
          </div>
        </form>
      </div>
    );
  };

  render() {
    return (
      <div>
        <this.sendPost />
      </div>
    );
  }
}
