import React from "react";
import { Form, Button } from "react-bootstrap";
import { Container, Row, Col } from "react-bootstrap";
import axios from "axios";
import Movie from "./Movie";

const server = "http://localhost:443/api";

export default class DisplayMovies extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      searchText: "",
      movieList: [],
    };
  }

  componentDidMount() {
    axios.get(`${server}/recommendations`).then((res) => {
      console.log(res.data);
      this.setState({ movieList: res.data });
      console.log(this.state.movieList);
    });
  }

  searchForm = () => {
    return (
      <Form onSubmit={(e) => this.onSubmitUpdatePost(e)}>
        <Form.Group controlId="exampleForm.ControlTextarea1">
          <Form.Control
            as="textarea"
            rows={5}
            value={this.state.updateTextPost}
            onChange={this.onChangeUpdatePost}
          />
        </Form.Group>

        <div className="w-100 d-flex justify-content-center">
          <Button className="mx-auto" type="submit">
            Edit
          </Button>
        </div>
      </Form>
    );
  };

  movieRecommendations = () => {
    return this.state.movieList.map((movie, index) => {
      return (
        <div key={index}>
          <Movie movie={movie} />
        </div>
      );
    });
  };

  render() {
    return (
      <div>
        <this.searchForm />
        <br></br>
        <div>
          <h2 align="center">Movie Recommendations</h2>
          <br></br>
          <this.movieRecommendations />
        </div>
      </div>
    );
  }
}
