import React, { Component } from "react";
import axios from "axios";
import "./login-register/login-register";
//import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import LoginRegister from "./login-register/login-register";

const server = "http://localhost:5000/api";

const Post = (props) => (
  <div className="post-box">
    <br></br>
    <div className="card w-75 mx-auto">
      <div className="card-body">
        <div class="media">
          <img src="..." class="mr-3" alt="..." />
          <div class="media-body">
            <h5 className="card-title">{props.posts.name} said:</h5>
            <p className="text-left">{props.posts.post}</p>
            <p className="card-text">posted: {props.posts.dateCreated}</p>
          </div>
        </div>
      </div>
      <div>
        <p>reply btn</p>
      </div>
    </div>
  </div>
);

export default class Wall extends Component {
  constructor(props) {
    super(props);

    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePost = this.onChangePost.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      postList: [],
      name: "",
      post: "",
    };
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangePost(e) {
    this.setState({
      post: e.target.value,
    });
  }

  onSubmit(e) {
    e.preventDefault();

    const post = {
      name: this.state.name,
      post: this.state.post,
    };

    axios
      .post(`${server}/addPost`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      name: "",
      post: "",
    });

    axios
      .get(`${server}/get`)
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));

    window.location = "/";
  }

  componentDidMount() {
    axios
      .get(`${server}/get`)
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));
  }

  postList() {
    console.log(this.state.postList);
    return this.state.postList.map((post) => {
      return <Post posts={post} />;
    });
  }

  render() {
    return (
      <div className="container-box">
        <div className="left-box">
          <p>ssfdd</p>
        </div>
        <div className="border content-box">
          <div className="empty-box"></div>

          <ul
            class="nav nav-tabs justify-content-center"
            id="myTab"
            role="tablist"
          >
            <li class="nav-item" role="presentation">
              <a
                class="nav-link active"
                id="home-tab"
                data-toggle="tab"
                href="#add-post"
                role="tab"
                aria-controls="home"
                aria-selected="true"
              >
                Send Post
              </a>
            </li>
            <li class="nav-item" role="presentation">
              <a
                class="nav-link"
                id="profile-tab"
                data-toggle="tab"
                href="#wall"
                role="tab"
                aria-controls="profile"
                aria-selected="false"
              >
                Wall
              </a>
            </li>
            <li class="nav-item" role="presentation">
              <a
                class="nav-link"
                id="contact-tab"
                data-toggle="tab"
                href="#mypost"
                role="tab"
                aria-controls="contact"
                aria-selected="false"
              >
                My Post
              </a>
            </li>
          </ul>
          <div class="tab-content" id="myTabContent">
            <div
              class="tab-pane fade show active"
              id="add-post"
              role="tabpanel"
              aria-labelledby="home-tab"
            >
              <div>
                <div className="add-post-box">
                  <h3>Post anything :)</h3>
                  <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                      <label for="name-input"></label>
                      <input
                        placeholder="Name"
                        className="form-control"
                        type="text"
                        required
                        value={this.state.name}
                        onChange={this.onChangeName}
                        id="name-input"
                      />
                    </div>
                    <div className="form-group" id="comment">
                      <textarea
                        placeholder="What do you think?"
                        className="form-control"
                        id="exampleFormControlTextarea1"
                        rows="3"
                        required
                        value={this.state.post}
                        onChange={this.onChangePost}
                      />
                    </div>
                    <div>
                      <input
                        type="submit"
                        value="Send"
                        className="btn btn-primary"
                      />
                    </div>
                  </form>
                </div>
              </div>
            </div>
            <div
              class="tab-pane fade"
              id="wall"
              role="tabpanel"
              aria-labelledby="profile-tab"
            >
              <div className="wall-post">
                <div>{this.postList()}</div>
              </div>
            </div>
            <div
              class="tab-pane fade"
              id="mypost"
              role="tabpanel"
              aria-labelledby="contact-tab"
            >
              <div>{LoginRegister.login}</div>
            </div>
          </div>
        </div>
        <div className="right-box">
          <p>fdsafaga</p>
        </div>
      </div>
    );
  }
}
