import React from "react";
import svg from "./svg-sources";
import "./style/Footer.css";

export default class Footer extends React.Component {
  contactLink = () => {
    const key = [
      svg.facebook,
      svg.twitter,
      svg.instagram,
      svg.github,
      svg.whatsapp,
    ];

    const link = [
      "https://web.facebook.com/usagii.yuki/",
      "https://twitter.com/rizkysaputraz",
      "https://www.instagram.com/rizkyysaputra/",
      "https://github.com/rizkysaputra4/",
      "https://wa.link/np11bd",
    ];

    return key.map((key, index) => {
      return (
        <div className="contact-icon px-2">
          <a target="_blank" href={link[index]} rel="noopener noreferrer">
            {key}
          </a>
        </div>
      );
    });
  };

  Footer = () => {
    return (
      <div className="footer-container pt-5 pb-5" id="footer-container">
        <div className="container d-flex justify-content-center pt-3 pb-3">
          <div className="contact-icon px-2">
            <a
              target="_blank"
              href="https://github.com/rizkysaputra4/"
              rel="noopener noreferrer"
            >
              {svg.facebook}
            </a>
          </div>
          <div className="contact-icon px-2">
            <a
              target="_blank"
              href="https://twitter.com/rizkysaputraz"
              rel="noopener noreferrer"
            >
              {svg.twitter}
            </a>
          </div>
          <div className="contact-icon px-2">
            <a
              target="_blank"
              href="https://twitter.com/rizkysaputraz"
              rel="noopener noreferrer"
            >
              {svg.instagram}
            </a>
          </div>
          <div className="contact-icon px-2">
            <a
              target="_blank"
              href="https://twitter.com/rizkysaputraz"
              rel="noopener noreferrer"
            >
              {svg.github}
            </a>
          </div>
          <div className="contact-icon px-2">
            <a
              target="_blank"
              href="https://twitter.com/rizkysaputraz"
              rel="noopener noreferrer"
            >
              {svg.whatsapp}
            </a>
          </div>
        </div>

        <p>© 2020 by Rizky Saputra</p>
      </div>
    );
  };

  render() {
    return <this.Footer />;
  }
}
