import React, { Component } from "react";
import axios from "axios";
import { Modal, Button, Form } from "react-bootstrap";

const server = "http://localhost:5000/api";

export default class UpdatePost extends Component {
  constructor(props) {
    super(props);

    this.onChangeUpdatePost = this.onChangeUpdatePost.bind(this);
    this.onSubmitUpdatePost = this.onSubmitUpdatePost.bind(this);
    this.callUpdateTextState = this.callUpdateTextState.bind(this);

    this.state = {
      updateTextPost: "",
    };
  }

  onChangeUpdatePost(e) {
    console.log(this.state.updateTextPost);
    this.setState({
      updateTextPost: e.target.value,
    });
  }

  callUpdateTextState() {
    return this.state.updateTextPost;
  }

  onSubmitUpdatePost(post_id) {
    console.log(post_id);
    const post = {
      post: this.state.updateTextPost,
      withCredentials: true,
    };

    axios
      .post(`${server}/updatePost/${post_id}`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      updateTextPost: "",
    });
  }

  functionMyVerticallyCenteredModal = (props) => {
    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Modal heading
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Centered Modal</h4>
          <UserConsumer>
            {(value) => {
              let defaultVal = value.post.post;
              const onChangeText = (e) => {
                defaultVal = e.target.value;
                console.log(defaultVal);
              };
              return (
                <Form
                  onSubmit={(e) => value.updateFormOnSubmit(value.post._id)}
                >
                  <Form.Group controlId="updatePostText">
                    <Form.Label>Example textarea</Form.Label>
                    <Form.Control
                      value={defaultVal}
                      as="textarea"
                      rows={3}
                      onChange={onChangeText}
                    />
                  </Form.Group>
                  <Button variant="primary" type="submit">
                    Submit
                  </Button>
                </Form>
              );
            }}
          </UserConsumer>
        </Modal.Body>
        <Modal.Footer>
          <Button>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  };

  App = (props) => {
    const [modalShow, setModalShow] = React.useState(false);

    return (
      <>
        <Button variant="primary" onClick={() => setModalShow(true)}>
          Edit Post
        </Button>

        <MyVerticallyCenteredModal
          show={modalShow}
          onHide={() => setModalShow(false)}
        />
      </>
    );
  };
}
