import React, { Component } from "react";
import axios from "axios";
import { Modal, Button, Form } from "react-bootstrap";

const server = "http://localhost:5000/api";

export default class EditPost extends Component {
  constructor(props) {
    super(props);

    this.onChangeUpdatePost = this.onChangeUpdatePost.bind(this);
    this.onSubmitUpdatePost = this.onSubmitUpdatePost.bind(this);

    this.state = {
      updateTextPost: "",
    };
  }

  componentDidMount() {
    axios
      .get(`${server}/get/${this.props.match.params.postID}`)
      .then((post) => {
        this.setState({ updateTextPost: post.data.post });
        console.log(this.state.updateTextPost);
      })
      .catch((err) => console.log(err));
  }

  onChangeUpdatePost(e) {
    console.log(this.state.updateTextPost);
    this.setState({
      updateTextPost: e.target.value,
    });
  }

  onSubmitUpdatePost() {
    const post = {
      post: this.state.updateTextPost,
      withCredentials: true,
    };

    axios
      .post(`${server}/updatePost/${this.props.match.params.postID}`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      updateTextPost: "",
    });
  }

  Example() {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
      <>
        <Button variant="primary" onClick={handleShow}>
          Launch demo modal
        </Button>

        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={handleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }

  render() {
    return (
      <div>
        <this.App />
        <Form onSubmit={this.onSubmitUpdatePost}>
          <Form.Group controlId="exampleForm.ControlTextarea1">
            <Form.Label>Example textarea</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={this.state.updateTextPost}
              onChange={this.onChangeUpdatePost}
            />
          </Form.Group>

          <Button type="submit">Edit Post</Button>
        </Form>
      </div>
    );
  }
}
