import React from "react";
import { Form, Button } from "react-bootstrap";
import { Container, Row, Col } from "react-bootstrap";

export default class Hobbies extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      searchText: "",
    };
  }

  searchForm = () => {
    return (
      <Form onSubmit={(e) => this.onSubmitUpdatePost(e)}>
        <Form.Group controlId="exampleForm.ControlTextarea1">
          <Form.Control
            as="textarea"
            rows={5}
            value={this.state.updateTextPost}
            onChange={this.onChangeUpdatePost}
          />
        </Form.Group>

        <div className="w-100 d-flex justify-content-center">
          <Button className="mx-auto" type="submit">
            Edit
          </Button>
        </div>
      </Form>
    );
  };

  movieRecommendations = () => {
    return (
      <Container>
        <Row>
          <Col xs={3} md={4}>
            <img
              src={require("../pic/movie.png")}
              className="img-thumbnail"
              alt="movie"
            />
            <h5 className="bottom-left mb-0 ml-2">Movies</h5>
          </Col>
          <Col xs={6} md={4}>
            <img
              src={require("../pic/movie.png")}
              className="img-thumbnail"
              alt="movie"
            />
            <h5 className="bottom-left mb-0 ml-2">Books</h5>
          </Col>
          <Col xs={6} md={4}>
            <img
              src={require("../pic/movie.png")}
              className="img-thumbnail"
              alt="movie"
            />
            <h5 className="bottom-left mb-0 ml-2">Cook</h5>
          </Col>
        </Row>
      </Container>
    );
  };

  render() {
    return (
      <div>
        <this.searchForm />
      </div>
    );
  }
}
