import React, { Component, useState } from "react";
import axios from "axios";
import { Modal, Button, Form } from "react-bootstrap";

const server = "http://localhost:5000/api";

export default class EditPost extends Component {
  constructor(props) {
    super(props);

    this.onChangeUpdatePost = this.onChangeUpdatePost.bind(this);
    this.onSubmitUpdatePost = this.onSubmitUpdatePost.bind(this);
    this.editPostForm = this.editPostForm.bind(this);
    this.modal = this.modal.bind(this);

    this.state = {
      updateTextPost: "",
    };
  }

  componentDidMount() {
    axios
      .get(`${server}/get/${this.props.postID}`)
      .then((post) => {
        this.setState({ updateTextPost: post.data.post });
        console.log(this.state.updateTextPost);
      })
      .catch((err) => console.log(err));
  }

  onChangeUpdatePost(e) {
    console.log(this.state.updateTextPost);
    this.setState({
      updateTextPost: e.target.value,
    });
  }

  onSubmitUpdatePost() {
    const post = {
      post: this.state.updateTextPost,
      withCredentials: true,
    };

    axios
      .post(`${server}/updatePost/${this.props.postID}`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      updateTextPost: "",
    });
  }

  editPostForm() {
    return (
      <Form onSubmit={this.onSubmitUpdatePost}>
        <Form.Group controlId="exampleForm.ControlTextarea1">
          <Form.Label>Example textarea</Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            value={this.state.updateTextPost}
            onChange={this.onChangeUpdatePost}
          />
        </Form.Group>

        <Button type="submit">Edit Post</Button>
      </Form>
    );
  }

  modal() {
    function Example() {
      const [show, setShow] = useState(false);

      const handleClose = () => setShow(false);
      const handleShow = () => setShow(true);

      return (
        <>
          <Button variant="primary" onClick={handleShow}>
            Launch demo modal
          </Button>

          <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Modal heading</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              Woohoo, you're reading this text in a modal!
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose}>
                Close
              </Button>
              <Button variant="primary" onClick={handleClose}>
                Save Changes
              </Button>
            </Modal.Footer>
          </Modal>
        </>
      );
    }

    return (
      <div>
        <button
          type="button"
          className="btn btn-primary"
          data-toggle="modal"
          data-target="#exampleModal"
        >
          Launch demo modal
        </button>

        <div
          className="modal fade"
          id="exampleModal"
          tabindex="-1"
          role="dialog"
          aria-labelledby="exampleModalLabel"
          aria-hidden="true"
        >
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">
                  Modal title
                </h5>
                <button
                  type="button"
                  className="close"
                  data-dismiss="modal"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">{this.editPostForm()}</div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-dismiss="modal"
                >
                  Close
                </button>
                <button type="button" className="btn btn-primary">
                  Save changes
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  render() {
    return (
      <div>
        <this.modal />
      </div>
    );
  }
}
