import React from "react";
import axios from "axios";
import SearchMovie from "./Search-movie";

const server = "http://localhost:443/api";

export default class MovieDetails extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      movie: "",
    };
  }

  componentDidMount() {
    axios
      .get(`${server}/getmovie/${this.props.match.params.imdbID}`)
      .then((res) => {
        this.setState({ movie: res.data });
        console.log(res.data);
      })
      .catch((err) => console.log(err));
  }

  ratings = () => {
    const movie = this.state.movie;
    if (movie.Ratings) {
      return movie.Ratings.map((rating, index) => {
        return (
          <li key={index}>
            Source{rating.Source}: {rating.Value}
          </li>
        );
      });
    }
  };

  displayDetails = () => {
    const movie = this.state.movie;
    return (
      <div className="container">
        <h3>
          {movie.Title} ({movie.Year})
        </h3>
        <div className="row">
          <div className="col-md-auto">
            <img
              src={movie.Poster}
              height="300"
              width="220"
              alt={movie.Title}
            />
          </div>
          <div className="col">
            <div className="row">
              <div className="col">Genre</div>{" "}
              <div className="col">{movie.Genre}</div>
            </div>

            <div className="row">
              <div className="col">Director</div>
              <div className="col">{movie.Director}}</div>
            </div>

            <div className="row">
              <div className="col">Writer</div>
              <div className="col">{movie.Writer}}</div>
            </div>

            <div className="row">
              <div className="col">Actor</div>
              <div className="col">{movie.Actors}}</div>
            </div>

            <div className="row">
              <div className="col">Language</div>
              <div className="col">{movie.Language}}</div>
            </div>
          </div>
        </div>
        <div>
          <ul>
            <li>
              IMDB Rating : {movie.imdbRating}/10 From {movie.imdbVotes} Votes
            </li>
            {this.ratings()}
          </ul>
        </div>
        <p>Rated : {movie.Rated}</p>
        <p>Year : {movie.Year}</p>
        <p>Runtime : {movie.Runtime}</p>
        <p>Type : {movie.Type}</p>
        <p>Plot: {movie.Plot}</p>
        <p>Awards: {movie.Awards}</p>
      </div>
    );
  };

  render() {
    return (
      <div>
        <SearchMovie />
        {this.displayDetails()}
      </div>
    );
  }
}
