import React, { Component } from "react";
import axios from "axios";
import AuthHandler from "./wall-component/Auth-Handler";
import "bootstrap/dist/css/bootstrap.min.css";
import Post from "./wall-component/Wall-Post";
import sendPost from "./wall-component/Send-post";
import SendPost from "./wall-component/Send-post";

const server = "http://localhost:5000/api";

export default class Wall extends React.Component {
  constructor(props) {
    super(props);

    this.onChangeReplyText = this.onChangeReplyText.bind(this);
    this.onSubmitReply = this.onSubmitReply.bind(this);

    this.state = {
      postList: [],
      name: "",
      post: "",
      replyList: [],

      updateTextPost: "",

      myPostList: [],
      userID: "",

      //login/register prop
      myUserName: "",
    };
  }

  shouldComponentUpdate(nextProps, nextState) {
    if (this.state.postList.length === nextState.postList.length) {
      return false;
    } else {
      return true;
    }
  }

  componentDidMount() {
    axios
      .get(`${server}/get`, { withCredentials: true })
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
        console.log(this.state.postList);
      })
      .catch((err) => console.log(err));

    axios
      .get(`${server}/getting/mypost`, { withCredentials: true })
      .then((res) => {
        const myPostList = res.data;
        this.setState({ myPostList });
      })
      .catch((err) => console.log(err));
  }

  ////////Add Post/////////

  ////////Wall Post////////////

  postListDisp() {
    if (!this.state.postList || this.state.postList.length === 0) {
      return (
        <div className="mt-5">
          <p>This Wall is Empty</p>
          <p>Click 'Send Post' tab to post something</p>
        </div>
      );
    } else {
      return this.state.postList.map((post) => {
        return (
          <div>
            <Post
              posts={post}
              key={post._id}
              replyTab={this.replyForm(post._id)}
              deletePostFunc={() => this.deletePost(post._id)}
              server={server}
              picture={this.generateProfilPicture}
            />
          </div>
        );
      });
    }
  }

  myPostListDisp() {
    if (this.state.isLoggedIn) {
      if (!this.state.myPostList || this.state.myPostList.length === 0) {
        return (
          <div className="mt-5">
            <p>You haven't post anything</p>
            <p>Visit 'Send Post' tab to post something</p>
          </div>
        );
      } else {
        return this.state.myPostList.map((post) => {
          return (
            <>
              <Post
                posts={post}
                key={post._id}
                replyTab={this.replyForm(post._id)}
                deletePostFunc={() => this.deletePost(post._id)}
                updatePost={() => this.formUpdatedPost(post._id, post.post)}
                server={server}
                picture={this.generateProfilPicture}
              />
            </>
          );
        });
      }
    } else {
      return (
        <div>
          <p>Oops you are log in as a guest, nothing to show here</p>
          <p>Log in or Sign Up to show your post here.</p>
        </div>
      );
    }
  }

  deletePost(post_id) {
    console.log(post_id);

    axios
      .delete(`${server}/deletepost/${post_id}`)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  }

  /////////////////COMMENT FORM//////////////////
  onChangeReplyText(e) {
    this.setState({
      replyText: e.target.value,
    });
  }

  onSubmitReply(e, post_id) {
    e.preventDefault();
    const reply = {
      userName: this.state.userName,
      replyText: this.state.replyText,
    };

    axios
      .post(`${server}/replyPost/${post_id}`, reply)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      replyText: "",
    });
  }

  replyForm(post_id) {
    return (
      <div>
        <form onSubmit={(e) => this.onSubmitReply(e, post_id)}>
          <div className="form-group" id="comment">
            <textarea
              placeholder="Write comment...."
              className="form-control"
              id="exampleFormControlTextarea1"
              rows="2"
              required
              onChange={this.onChangeReplyText}
            />
          </div>
          <div>
            <input
              type="submit"
              value="Send Reply"
              className="btn btn-primary btn-sm"
            />
          </div>
        </form>
      </div>
    );
  }

  generateProfilPicture() {
    const sprites = ["male", "female", "human", "bottts", "avataaars", "gridy"];

    let sprite = sprites[Math.floor(Math.random() * sprites.length)];
    let seed = Math.floor(Math.random() * 10000);

    let picture = `https://avatars.dicebear.com/api/${sprite}/${seed}.svg`;
    return picture;
  }

  render() {
    return (
      <div className="container-box">
        <div className="left-box" id="left-box">
          <p>ssfdd</p>
        </div>
        <div className="border content-box">
          <div className="empty-box"></div>

          <ul
            className="nav nav-tabs justify-content-center"
            id="myTab"
            role="tablist"
          >
            <li className="nav-item" role="presentation">
              <a
                className="nav-link"
                id="home-tab"
                data-toggle="tab"
                href="#add-post"
                role="tab"
                aria-controls="home"
                aria-selected="true"
              >
                Send Post
              </a>
            </li>
            <li className="nav-item" role="presentation">
              <a
                className="nav-link active"
                id="profile-tab"
                data-toggle="tab"
                href="#wall"
                role="tab"
                aria-controls="profile"
                aria-selected="false"
              >
                Wall
              </a>
            </li>
            <li className="nav-item" role="presentation">
              <a
                className="nav-link"
                id="contact-tab"
                data-toggle="tab"
                href="#mypost"
                role="tab"
                aria-controls="contact"
                aria-selected="false"
              >
                My Post
              </a>
            </li>
          </ul>
          <div className="tab-content" id="myTabContent">
            <div
              className="tab-pane"
              id="add-post"
              role="tabpanel"
              aria-labelledby="home-tab"
            >
              <SendPost server={server} />
            </div>
            <div
              className="tab-pane fade show active"
              id="wall"
              role="tabpanel"
              aria-labelledby="profile-tab"
            >
              <div className="wall-post ">
                <div>{this.postListDisp()}</div>
              </div>
            </div>
            <div
              className="tab-pane fade"
              id="mypost"
              role="tabpanel"
              aria-labelledby="contact-tab"
            >
              <div>
                <div className="mt-5">
                  <div>{this.myPostListDisp()}</div>
                </div>
                <div className="text-center"></div>
              </div>
            </div>
          </div>
        </div>
        <div className="right-box">
          <AuthHandler isLoggedIn={this.state.isLoggedIn} server={server} />
        </div>
      </div>
    );
  }
}
