import React, { Component } from "react";
import axios from "axios";
//import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const server = "http://localhost:5000/api";

const Post = (props) => (
  <div className="post-box">
    <br></br>
    <div className="card w-75 mx-auto">
      <div className="card-body">
        <div className="media">
          <img src="..." className="mr-3" alt="..." />
          <div className="media-body">
            <h5 className="card-title">{props.posts.name} said:</h5>
            <p className="text-left">{props.posts.post}</p>
            <p className="card-text">posted: {props.posts.dateCreated}</p>
          </div>
        </div>
      </div>
      <div>
        <p>reply btn</p>
      </div>
    </div>
  </div>
);

export default class Wall extends Component {
  constructor(props) {
    super(props);

    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePost = this.onChangePost.bind(this);
    this.onSubmitPost = this.onSubmitPost.bind(this);

    this.onChangeLoginEmail = this.onChangeLoginEmail.bind(this);
    this.onChangeLoginPassword = this.onChangeLoginPassword.bind(this);
    this.onSubmitLogin = this.onSubmitLogin.bind(this);

    this.onChangeRegisterUsername = this.onChangeRegisterUsername.bind(this);
    this.onChangeRegisterEmail = this.onChangeRegisterEmail.bind(this);
    this.onChangeRegisterPassword = this.onChangeRegisterPassword.bind(this);
    this.onSubmitRegister = this.onSubmitRegister.bind(this);

    this.state = {
      postList: [],
      name: "",
      post: "",

      //login/register prop
      email: "",
      password: "",
      userName: "",
    };
  }

  ////////Add Post/////////
  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangePost(e) {
    this.setState({
      post: e.target.value,
    });
  }

  onSubmitPost(e) {
    e.preventDefault();

    const post = {
      name: this.state.name,
      post: this.state.post,
      withCredentials: true,
    };

    axios
      .post(`${server}/addPost`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      name: "",
      post: "",
    });

    axios
      .get(`${server}/get`)
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));
  }

  //////////login//////////////
  onChangeLoginEmail(e) {
    this.setState({
      email: e.target.value,
    });
  }

  onChangeLoginPassword(e) {
    this.setState({
      password: e.target.value,
    });
  }

  onSubmitLogin(e) {
    e.preventDefault();

    const loginProps = {
      email: this.state.email,
      password: this.state.password,
    };

    axios
      .post(`${server}/login`, loginProps)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      email: "",
      password: "",
    });

    console.log(loginProps);
  }

  //////////Register///////////
  onChangeRegisterUsername(e) {
    this.setState({
      userName: e.target.value,
    });
  }

  onChangeRegisterEmail(e) {
    this.setState({
      email: e.target.value,
    });
  }

  onChangeRegisterPassword(e) {
    this.setState({
      password: e.target.value,
    });
  }

  onSubmitRegister(e) {
    e.preventDefault();

    const registerProps = {
      userName: this.state.userName,
      email: this.state.email,
      password: this.state.password,
    };

    axios
      .post(`${server}/signup`, registerProps)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      userName: "",
      email: "",
      password: "",
    });

    console.log(registerProps);
  }

  ////////Wall Post////////////
  componentDidMount() {
    axios
      .get(`${server}/get`, { withCredentials: true })
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));
  }

  postList() {
    console.log(this.state.postList);
    return this.state.postList.map((post) => {
      return <Post posts={post} />;
    });
  }

  loginForm() {
    return (
      <div className="card card-body mb-5">
        <div className="modal-header">
          <h5 className="modal-title" id="exampleModalLabel">
            Login
          </h5>
        </div>
        <div className="modal-body">
          <form onSubmit={this.onSubmitLogin}>
            <div className="form-group">
              <label htmlFor="login-email" className="col-form-label">
                Email
              </label>
              <input
                type="text"
                className="form-control"
                id="login-email"
                required
                value={this.state.email}
                onChange={this.onChangeLoginEmail}
              />
            </div>
            <div className="form-group">
              <label htmlFor="login-password" className="col-form-label">
                Password
              </label>
              <input
                type="text"
                className="form-control"
                id="login-password"
                required
                value={this.state.password}
                onChange={this.onChangeLoginPassword}
              />
            </div>

            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                data-dismiss="modal"
              >
                Close
              </button>
              <button type="submit" value="send" className="btn btn-primary">
                Login
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }

  registerForm() {
    return (
      <div className="card card-body mb-5">
        <div className="modal-header">
          <h5 className="modal-title" id="exampleModalLabel">
            Register
          </h5>
        </div>
        <div className="modal-body">
          <form onSubmit={this.onSubmitRegister}>
            <div className="form-group">
              <label htmlFor="recipient-name" className="col-form-label">
                Username
              </label>
              <input
                type="text"
                className="form-control"
                id="recipient-name"
                required
                value={this.state.userName}
                onChange={this.onChangeRegisterUsername}
              />
            </div>
            <div className="form-group">
              <label htmlFor="recipient-name" className="col-form-label">
                Email
              </label>
              <input
                type="text"
                className="form-control"
                id="recipient-name"
                required
                value={this.state.email}
                onChange={this.onChangeRegisterEmail}
              />
            </div>
            <div className="form-group">
              <label htmlFor="recipient-name" className="col-form-label">
                Password
              </label>
              <input
                type="text"
                className="form-control"
                id="recipient-name"
                required
                value={this.state.password}
                onChange={this.onChangeRegisterPassword}
              />
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                data-dismiss="modal"
              >
                Close
              </button>
              <button type="sumbit" value="send" className="btn btn-primary">
                Register
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }

  loginRegisterModalBox() {
    return (
      <div>
        <div className="modal-dialog">
          <div className="modal-content modal-dialog-centered" id="myGroup">
            <div className="justify-content-center mx-auto mt-1">
              <h5 className=" mt-4 text-center">Click Below to</h5>
              <div className="d-flex mt-4 mb-4">
                <a
                  className="btn btn-primary"
                  data-toggle="collapse"
                  href="#login"
                  role="button"
                  aria-expanded="false"
                  aria-controls="login"
                >
                  Log In
                </a>
                <h5 className="ml-3 mr-3 mt-1"> Or </h5>
                <button
                  className="btn btn-primary"
                  type="button"
                  data-toggle="collapse"
                  data-target="#signup"
                  aria-expanded="false"
                  aria-controls="signup"
                >
                  Register
                </button>
              </div>
            </div>
            <div className="collapse" id="login" data-parent="#myGroup">
              {this.loginForm()}
            </div>
            <div className="collapse" id="signup" data-parent="#myGroup">
              {this.registerForm()}
            </div>
          </div>
        </div>
      </div>
    );
  }

  sendPost() {
    return (
      <div className="add-post-box">
        <h3>Post anything :)</h3>
        <form onSubmit={this.onSubmitPost}>
          <div className="form-group">
            <label htmlFor="name-input"></label>
            <input
              placeholder="Name"
              className="form-control"
              type="text"
              required
              value={this.state.name}
              onChange={this.onChangeName}
              id="name-input"
            />
          </div>
          <div className="form-group" id="comment">
            <textarea
              placeholder="What do you think?"
              className="form-control"
              id="exampleFormControlTextarea1"
              rows="3"
              required
              value={this.state.post}
              onChange={this.onChangePost}
            />
          </div>
          <div>
            <input type="submit" value="Send" className="btn btn-primary" />
          </div>
        </form>
      </div>
    );
  }

  render() {
    return (
      <div className="container-box">
        <div className="left-box">
          <p>ssfdd</p>
        </div>
        <div className="border content-box">
          <div className="empty-box"></div>

          <ul
            className="nav nav-tabs justify-content-center"
            id="myTab"
            role="tablist"
          >
            <li className="nav-item" role="presentation">
              <a
                className="nav-link active"
                id="home-tab"
                data-toggle="tab"
                href="#add-post"
                role="tab"
                aria-controls="home"
                aria-selected="true"
              >
                Send Post
              </a>
            </li>
            <li className="nav-item" role="presentation">
              <a
                className="nav-link"
                id="profile-tab"
                data-toggle="tab"
                href="#wall"
                role="tab"
                aria-controls="profile"
                aria-selected="false"
              >
                Wall
              </a>
            </li>
            <li className="nav-item" role="presentation">
              <a
                className="nav-link"
                id="contact-tab"
                data-toggle="tab"
                href="#mypost"
                role="tab"
                aria-controls="contact"
                aria-selected="false"
              >
                My Post
              </a>
            </li>
          </ul>
          <div className="tab-content" id="myTabContent">
            <div
              className="tab-pane fade show active"
              id="add-post"
              role="tabpanel"
              aria-labelledby="home-tab"
            >
              <div>{this.sendPost()}</div>
            </div>
            <div
              className="tab-pane fade"
              id="wall"
              role="tabpanel"
              aria-labelledby="profile-tab"
            >
              <div className="wall-post">
                <div>{this.postList()}</div>
              </div>
            </div>
