import React from "react";
import axios from "axios";

export default class SearchResults extends React.Component {
  constructor(props) {
    super(props);
  }

  onClickResult = () => {
    axios
      .get(`${this.props.server}/getmovie/${this.props.movie.imdbID}`)
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => console.log(err));
  };

  displayResult = () => {
    const movie = this.props.movie;
    return (
      <div
        className="card h-auto w-75 mx-auto "
        id="post-box"
        onClick={() => this.onClickResult()}
      >
        <div className="card-body mb-1 pb-0 border-bottom" id="post-cont">
          <div className="container h-auto pb-0 mx-0 px-0">
            <div className="d-flex flex-row">
              <h3 className="card-title h-auto">
                {movie.Title} ({movie.Year})
              </h3>
            </div>
          </div>

          <div>
            <p id="date" className="text-sm-left mb-1">
              Type: {movie.Type}
            </p>
          </div>
          <div className="media" id="post-box">
            <img
              src={movie.Poster}
              height="180"
              width="140"
              className="mr-3 mb-2"
              alt={movie.Title}
            />
          </div>
        </div>
      </div>
    );
  };

  render() {
    return <this.displayResult />;
  }
}
