import React, { Component } from "react";
import axios from "axios";
import "./style/Home.css";
import { Card, CardGroup } from "react-bootstrap";
//import { Link } from "react-router-dom";

const server = "http://localhost:5000/api";

export default class Home extends Component {
  /* constructor(props) {
    super(props);

    this.state = {
      loginStats: false,
    };
  } */

  componentDidMount() {
    axios
      .get(`${server}/checkLogin`)
      .then((res) => {
        console.log(res.data);
        if (!res.data.login) {
          console.log("generate token");
          axios.post(`${server}/generateGuestToken`).then((res) => {
            console.log(res.data);
          });
        }
      })
      .catch((err) => console.log(err));
  }

  render() {
    return (
      <div>
        <div id="Welcome">
          <h3>Welcome</h3>
        </div>
        <div className="m-0 p-0 w-auto">
          <p id="name">I'am Rizky Saputra</p>
        </div>
        <div id="profile">
          <p id="profile1">
            A graduate from University of Lampung in BEng Electrical Engineering
            with specialization in Telecommunications and Information
            Technology.
          </p>
          <p id="profile2">
            Someone with good learning ability, hard worker, and have good
            skills to think analytically
          </p>
        </div>
        <div className="w-50 d-flex justify-content-center" id="skills">
          <h5>Skills</h5>
          <ul>
            <CardGroup>
              <Card>
                <Card.Body>Javascript</Card.Body>
              </Card>
              <Card>
                <Card.Body>ReactJs</Card.Body>
              </Card>
              <Card>
                <Card.Body>Express</Card.Body>
              </Card>
              <Card>
                <Card.Body>MongoDB</Card.Body>
              </Card>
            </CardGroup>
            <CardGroup>
              <Card>
                <Card.Body>NodeJs</Card.Body>
              </Card>
              <Card>
                <Card.Body>Matlab</Card.Body>
              </Card>
              <Card>
                <Card.Body>Bootstrap</Card.Body>
              </Card>
              <Card>
                <Card.Body>Arduino</Card.Body>
              </Card>
            </CardGroup>
          </ul>
        </div>
        <div id="contacts">
          <h6>Contact me</h6>
          <ul>
            <li>Email: rizkysaputra000@gmail.com</li>
            <li>Phone: 082186326036</li>
            <li>WA: 082186326036</li>
            <li>Github: https://github.com/rizkysaputra4/</li>
          </ul>
        </div>
      </div>
    );
  }
}
