import React from "react";
import SearchResults from "./Search-result";
import axios from "axios";
import SearchMovie from "./Search-movie";

const server = "http://localhost:443/api";
export default class SearchPage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      searchResult: [],
    };
  }

  componentDidMount() {
    console.log("server", this.props.location.server);
    console.log("movie", this.props.location.movie);
    console.log("leyword", this.props.match.params.keyword);
    console.log("page", this.props.match.params.page);

    axios
      .get(
        `${server}/searchmovie/${this.props.match.params.keyword}/${this.props.match.params.page}`
      )
      .then((res) => {
        this.setState({ searchResult: res.data.Search });
        console.log("search res", res.data);
      });
  }

  componentDidUpdate(prevProps) {
    if (
      prevProps.match.params.keyword !== this.props.match.params.keyword ||
      prevProps.match.params.page !== this.props.match.params.page
    ) {
      setState({
        type: this.props.match.params.type,
      });
    }
  }

  displaySearchResult = () => {
    if (!this.state.searchResult) {
      return (
        <div>
          <h5>Movie with Title "{this.state.searchKeyword}" is not found</h5>
        </div>
      );
    } else {
      return this.state.searchResult.map((movie, index) => {
        return (
          <div key={index}>
            <SearchResults movie={movie} server={this.props.location.server} />
          </div>
        );
      });
    }
  };

  updateSearchResult = (result) => {
    this.setState({ searchResult: result });
    console.log("updating search result");
  };

  render() {
    return (
      <>
        <SearchMovie updateResult={this.updateSearchResult} server={server} />
        <div className="container-fluid">
          <div className="row flex-md-wrap mx-auto justify-content-center">
            <this.displaySearchResult />
          </div>
        </div>
      </>
    );
  }
}
