import React, { Component } from "react";
import axios from "axios";
//import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const server = "http://localhost:5000/api";

const Post = (props) => (
  <div className="post-box">
    <br></br>
    <div class="card w-75 mx-auto">
      <div class="card-body">
        <h5 class="card-title p-3 mb-2 bg-primary text-white">
          {props.posts.name}
        </h5>
        <p className="text-left">{props.posts.post}</p>
        <p class="card-text">posted: {props.posts.dateCreated}</p>
      </div>
      <div>
        <p>reply btn</p>
      </div>
    </div>
  </div>
);

const postSomething = () => {};

export default class Wall extends Component {
  constructor(props) {
    super(props);

    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePost = this.onChangePost.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      postList: [],
      name: "",
      post: "",
    };
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangePost(e) {
    this.setState({
      post: e.target.value,
    });
  }

  onSubmit(e) {
    e.preventDefault();

    const post = {
      name: this.state.name,
      post: this.state.post,
    };

    axios
      .post(`${server}/addPost`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      name: "",
      post: "",
    });

    axios
      .get(`${server}/get`)
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));
  }

  componentDidMount() {
    axios
      .get(`${server}/get`)
      .then((res) => {
        const postList = res.data;
        this.setState({ postList });
      })
      .catch((err) => console.log(err));
  }

  postList() {
    console.log(this.state.postList);
    return this.state.postList.map((post) => {
      return <Post posts={post} />;
    });
  }

  render() {
    return (
      <div className="container-box">
        <div className="left-box">
          <p>ssfdd</p>
        </div>
        <div className="content-box">
          <div className="empty-box"></div>

          <div className="wall-post">
            <h3>Scratch on the Wall</h3>
            <div>{this.postList()}</div>
          </div>
        </div>
        <div className="right-box">
          <p>fdsafaga</p>
        </div>
      </div>
    );
  }
}
