import React from "react";
import axios from "axios";
import SearchMovie from "./Search-movie";
import "./movie-details.css";

const server = "http://localhost:443/api";

export default class MovieDetails extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      movie: "",
    };
  }

  componentDidMount() {
    axios
      .get(`${server}/getmovie/${this.props.match.params.imdbID}`)
      .then((res) => {
        this.setState({ movie: res.data });
        console.log(res.data);
      })
      .catch((err) => console.log(err));
  }

  ratings = () => {
    const movie = this.state.movie;
    if (movie.Ratings) {
      return movie.Ratings.map((rating, index) => {
        return (
          <p key={index}>
            {rating.Source}: {rating.Value}
          </p>
        );
      });
    }
  };

  displayDetails = () => {
    const movie = this.state.movie;
    return (
      <div className="container w-75 movie-container mt-0 p-4">
        <h3>
          {movie.Title} ({movie.Year})
        </h3>
        <p className="badge badge-pill badge-secondary">{movie.Rated} </p>
        <p className="badge badge-pill badge-secondary">{movie.Type} </p>
        <p className="badge badge-pill badge-secondary">
          Language: {movie.Language}
        </p>
        <div className="row">
          <div className="col-md-auto">
            <img
              src={movie.Poster}
              height="300"
              width="220"
              alt={movie.Title}
            />
          </div>
          <div className="col">
            <div className="container-fluid">
              <div className="row">
                <div className="col-2 ">Genre: </div>{" "}
                <div className="col">{movie.Genre}</div>
              </div>

              <div className="row">
                <div className="col-2 ">Director: </div>
                <div className="col">{movie.Director}</div>
              </div>

              <div className="row">
                <div className="col-2 key-movie">Writer: </div>
                <div className="col">{movie.Writer}</div>
              </div>

              <div className="row">
                <div className="col-2 key-movie">Actor: </div>
                <div className="col">{movie.Actors}</div>
              </div>

              <div className="row">
                <div className="col-2 key-movie">Runtime: </div>
                <div className="col">{movie.Runtime}</div>
              </div>

              <div className="row">
                <div className="col-2 key-movie">Released: </div>
                <div className="col">{movie.Released}</div>
              </div>

              <div className="row">
                <div className="col-2 key-movie">Rating: </div>
                <div className="col">
                  {movie.imdbRating}/10 From {movie.imdbVotes} Votes
                </div>
              </div>
            </div>
          </div>
        </div>

        <p className="mt-3 text-justify">{movie.Plot}</p>

        {/* <div className="">
          <h6>Rating: </h6>
          <div>{this.ratings()}</div>
        </div> */}
        <p className="text-left">Awards: {movie.Awards}</p>
      </div>
    );
  };

  render() {
    return (
      <div>
        <SearchMovie />
        <hr className="mb-0"></hr>
        {this.displayDetails()}
      </div>
    );
  }
}
