import React, { Component } from "react";
import axios from "axios";
import { Modal, Button, Form } from "react-bootstrap";
import { UserConsumer } from "../context";

const server = "http://localhost:5000/api";

export default class EditPost extends Component {
  constructor(props) {
    super(props);

    this.state = {
      defaultText: "",
    };
  }

  onChangeUpdatePost(e) {
    console.log(this.state.updateTextPost);
    this.setState({
      updateTextPost: e.target.value,
    });
  }

  callUpdateTextState() {
    return this.state.updateTextPost;
  }

  onSubmitUpdatePost(post_id) {
    console.log(post_id);
    const post = {
      post: this.state.updateTextPost,
      withCredentials: true,
    };

    axios
      .post(`${server}/updatePost/${post_id}`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      updateTextPost: "",
    });
  }

  render() {
    return (
      <div>
        <Form>
          <Form.Group controlId="exampleForm.ControlTextarea1">
            <Form.Label>Example textarea</Form.Label>
            <Form.Control as="textarea" rows={3} />
          </Form.Group>

          <button type="submit">Edit Post</button>
        </Form>
      </div>
    );
  }
}
