import React from "react";
import axios from "axios";
import { toast } from "react-toastify";

export default class SendPost extends React.Component {
  constructor(props) {
    super(props);
    this.onChangePost = this.onChangePost.bind(this);
    this.onSubmitPost = this.onSubmitPost.bind(this);
    this.state = {
      post: "",
    };
  }

  onChangePost(e) {
    this.setState({
      post: e.target.value,
    });
  }

  onSubmitPost(e) {
    e.preventDefault();
    const post = {
      post: this.state.post,
      withCredentials: true,
    };

    axios
      .post(`${this.props.server}/addPost`, post)
      .then((res) => {
        console.log(res.data);
        this.notify(res.data);
        const postList = this.props.postList.reverse();
        postList.push(res.data);
        console.log(postList);
        this.props.updatePostList(postList.reverse(), this.props.myPostList);
      })
      .catch((err) => console.log(err));

    this.setState({ post: "" });
  }

  notify = (alert) => {
    const options = {
      position: "top-center",
      autoClose: 5000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    };

    toast.success(`${alert}`, options);
  };

  sendPost = () => {
    return (
      <div className="add-post-box">
        <h3>Post anything :)</h3>
        <form onSubmit={(e) => this.onSubmitPost(e)}>
          <div className="form-group" id="comment">
            <textarea
              placeholder="What do you think?"
              className="form-control"
              id="exampleFormControlTextarea1"
              rows="6"
              required
              value={this.state.post}
              onChange={this.onChangePost}
            />
          </div>
          <div>
            <input type="submit" value="Send" className="btn btn-primary" />
          </div>
        </form>
      </div>
    );
  };

  render() {
    return (
      <div>
        <this.sendPost />
      </div>
    );
  }
}
