import React, { Component } from "react";
import axios from "axios";
import "./style/Home.css";
import svg from "./svg-sources";
import { TimelineLite, Bounce } from "gsap";

const server = "http://localhost:5000/api";

export default class Home extends Component {
  constructor(props) {
    super(props);
    this.HelloText = null;
    this.Hello = new TimelineLite({ paused: true });

    this.Rizky = null;
    this.RizkyEffect = new TimelineLite({ paused: true });

    this.myName = null;
    this.myNameEffect = new TimelineLite({ paused: true });
  }

  componentDidMount() {
    axios
      .get(`${server}/checkLogin`)
      .then((res) => {
        console.log(res.data);
        if (!res.data.login) {
          console.log("generate token");
          axios.post(`${server}/generateGuestToken`).then((res) => {
            console.log(res.data);
          });
        }
      })
      .catch((err) => console.log(err));

    this.RizkyEffect.to(this.Rizky, 0, { opacity: 0, x: 500 }, 1)
      .to(this.Rizky, 1, { opacity: 1, x: 0 })
      .play();

    this.Hello.to(this.HelloText, 0, { opacity: 0, y: -100 })
      .to(this.HelloText, 2.5, { opacity: 1, ease: Bounce.easeOut, y: 0 })
      .play();

    this.myNameEffect
      .to(this.myName, 0, { opacity: 0 }, 0.5)
      .to(this.myName, 1, { opacity: 1 })
      .play();
  }

  render() {
    return (
      <div>
        <div id="Welcome-container">
          <div style={{ color: "white" }} id="welcome-content">
            <h3 id="hello" ref={(div) => (this.HelloText = div)}>
              Hello,
            </h3>
            <div style={{ display: "flex" }} id="name">
              <pre style={{ color: "white" }}>My Name is </pre>{" "}
              <p style={{ opacity: 0 }} ref={(div) => (this.Rizky = div)}>
                Rizky Saputra
              </p>
            </div>
          </div>
        </div>

        <div id="and-container">
          <p id="and">And . . .</p>
        </div>

        <div id="container">
          <div id="profile-container">
            <div className="profile-row" id="I-container">
              <h1 id="I">I</h1>
            </div>
            <div className="profile-row" id="profile-list">
              <ul id="profile-list">
                <li>Am 24 Years Old</li>
                <li>
                  Have graduated from university of Lampung in Electrical
                  Engineering
                </li>
                <li>Live in Lampung Province</li>
                <li>Love Computer programming</li>
              </ul>
            </div>
          </div>
        </div>

        <div id="skill-box">
          <h5 id="my-skills" align="center">
            My Skills
          </h5>
          <div id="skills-container">
            <div id="skill-list">
              <ul>
                <li className="skill-name">{svg.mongoDB}</li>
                <li className="skill-name">{svg.express}</li>
                <li className="skill-name">
                  {svg.reactjs}
                  ReactJs
                </li>
                <li className="skill-name">{svg.nodeJs}</li>
              </ul>
              <ul>
                <li className="skill-name">{svg.javascript}Javascript</li>
                <li className="skill-name">
                  {svg.matlab}
                  Matlab
                </li>
                <li className="skill-name">
                  {svg.arduino}
                  Arduino
                </li>
                <li className="skill-name">
                  {svg.bootstrap}
                  Bootstrap
                </li>
              </ul>
            </div>
          </div>
        </div>
        <div id="hope">
          <h3 align="center">Hope we can work together</h3>
          <div id="cv" align="center">
            <a
              target="_blank"
              href="https://drive.google.com/file/d/12yHWXSegaYidHD9w1I_9Oe_nWdpi_iOx/view?usp=sharing"
            >
              My CV
            </a>
          </div>
        </div>
        <div id="contact-container">
          <div id="contacts">
            <h6>Contact me</h6>
            <ul>
              <li>Email: rizkysaputra000@gmail.com</li>
              <li>Phone: 082186326036</li>
              <li>WA: 082186326036</li>
              <li>
                <a target="_blank" href="https://github.com/rizkysaputra4/">
                  Github
                </a>
              </li>
            </ul>
          </div>
        </div>
      </div>
    );
  }
}
