import React from "react";
import { Form, Button } from "react-bootstrap";
import { Container, Row, Col } from "react-bootstrap";
import axios from "axios";
import Movie from "./Movie";

const server = "http://localhost:443/api";

export default class DisplayMovies extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      searchText: "",
      movieList: [],
    };
  }

  componentDidMount() {
    axios.get(`${server}/recommendations`).then((res) => {
      console.log(res.data);
      this.setState({ movieList: res.data });
      console.log(this.state.movieList);
    });
  }

  searchForm = () => {
    return (
      <div align="center">
        <Form>
          <Form.Row className="align-items-center mt-2 mx-auto">
            <Col xs={7}>
              <Form.Label htmlFor="inlineFormInput" srOnly>
                Name
              </Form.Label>
              <Form.Control
                className="mb-2"
                id="inlineFormInput"
                placeholder="Movie Title"
              />
            </Col>
            <Col xs="auto">
              <Button type="submit" className="mb-2">
                Search
              </Button>
            </Col>
          </Form.Row>
        </Form>
      </div>
    );
  };

  movieRecommendations = () => {
    return this.state.movieList.map((movie, index) => {
      return (
        <div key={index}>
          <Movie movie={movie} />
        </div>
      );
    });
  };

  render() {
    return (
      <div>
        <this.searchForm />
        <br></br>
        <div>
          <h2 align="center">Movie Recommendations</h2>
          <br></br>
          <this.movieRecommendations />
        </div>
      </div>
    );
  }
}
