import React from "react";
import SearchResults from "./Search-result";
import axios from "axios";
import SearchMovie from "./Search-movie";
import { Pagination } from "react-bootstrap";
import { Link } from "react-router-dom";

const server = "http://localhost:443/api";
export default class SearchPage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      searchResult: [],
    };
  }

  componentDidMount() {
    console.log("server", this.props.location.server);
    console.log("movie", this.props.location.movie);
    console.log("leyword", this.props.match.params.keyword);
    console.log("page", this.props.match.params.page);

    axios
      .get(
        `${server}/searchmovie/${this.props.match.params.keyword}/${this.props.match.params.page}`
      )
      .then((res) => {
        this.setState({ searchResult: res.data });
        console.log("search res", res.data);
      });
  }

  componentDidUpdate(prevProps) {
    if (
      prevProps.match.params.keyword !== this.props.match.params.keyword ||
      prevProps.match.params.page !== this.props.match.params.page
    ) {
      axios
        .get(
          `${server}/searchmovie/${this.props.match.params.keyword}/${this.props.match.params.page}`
        )
        .then((res) => {
          this.setState({ searchResult: res.data });
        });
    }
  }

  displaySearchResult = () => {
    if (!this.state.searchResult.Search) {
      return (
        <div>
          <h5>
            Movie with Title "{this.props.match.params.keyword}" is not found
          </h5>
        </div>
      );
    } else {
      return this.state.searchResult.Search.map((movie, index) => {
        return (
          <div key={index}>
            <SearchResults movie={movie} server={this.props.location.server} />
          </div>
        );
      });
    }
  };

  updateSearchResult = (result) => {
    this.setState({ searchResult: result });
  };

  pageButton = () => {
    console.log("aaaaaaaaaaaaaaaaaaa");
    let i;
    let pageMax;
    let pageNow = this.props.match.params.page;
    let arr = [];
    const totalResult = this.state.searchResult.totalResults;
    if (totalResult % 10 === 0) {
      pageMax = Math.floor(totalResult / 10);
    } else {
      pageMax = Math.floor(totalResult / 10) + 1;
    }

    for (i = pageNow - 3; i <= pageNow + 3; i++) {
      console.log("pageNow in Loop", i);
      console.log("pageNOw + 3", pageNow + 3);
      if (i > 0 && i <= pageMax) {
        arr.push(i);
      }
    }

    console.log("searchResult", this.state.searchResult);
    console.log("totalResult", totalResult);
    console.log("pageNow", pageNow);
    console.log("pagemax", pageMax);
    console.log("pageArr", arr);

    return arr.map((page, index) => {
      if (page === this.props.match.params.page) {
        return (
          <Pagination.Item key={index} active>
            {page}
          </Pagination.Item>
        );
      } else {
        return (
          <Link to={`/search-movie/${this.props.match.params.keyword}/${page}`}>
            <Pagination.Item key={index}>{page}</Pagination.Item>
          </Link>
        );
      }
    });
  };

  render() {
    return (
      <>
        <SearchMovie updateResult={this.updateSearchResult} server={server} />
        <div className="container-fluid">
          <div className="row flex-md-wrap mx-auto justify-content-center">
            <this.displaySearchResult />
          </div>
          <div className="d-flex justify-content-center mt-2">
            <this.pageButton />
          </div>
        </div>
      </>
    );
  }
}
