import React, { Component } from "react";
import axios from "axios";
import { Modal, Button, Form } from "react-bootstrap";

const server = "http://localhost:5000/api";

export default class EditPost extends Component {
  constructor(props) {
    super(props);

    this.onChangeUpdatePost = this.onChangeUpdatePost.bind(this);
    this.onSubmitUpdatePost = this.onSubmitUpdatePost.bind(this);

    this.state = {
      updateTextPost: "",
    };
  }

  componentDidMount() {
    axios
      .get(`${server}/get/${this.props.match.params.postID}`)
      .then((post) => {
        this.setState({ updateTextPost: post.data.post });
        console.log(this.state.updateTextPost);
      })
      .catch((err) => console.log(err));
  }

  onChangeUpdatePost(e) {
    console.log(this.state.updateTextPost);
    this.setState({
      updateTextPost: e.target.value,
    });
  }

  onSubmitUpdatePost() {
    const post = {
      post: this.state.updateTextPost,
      withCredentials: true,
    };

    axios
      .post(`${server}/updatePost/${this.props.match.params.postID}`, post)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));

    this.setState({
      updateTextPost: "",
    });
  }

  VerticallyCenteredModal(props) {
    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Modal heading
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Centered Modal</h4>
          <p>
            Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
            dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta
            ac consectetur ac, vestibulum at eros.
          </p>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }

  App() {
    const [modalShow, setModalShow] = React.useState(false);

    return (
      <>
        <Button variant="primary" onClick={() => setModalShow(true)}>
          vertically centered modal
        </Button>

        <this.VerticallyCenteredModal
          show={modalShow}
          onHide={() => setModalShow(false)}
        />
      </>
    );
  }

  render() {
    return (
      <div>
        <this.App />
        <Form onSubmit={this.onSubmitUpdatePost}>
          <Form.Group controlId="exampleForm.ControlTextarea1">
            <Form.Label>Example textarea</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={this.state.updateTextPost}
              onChange={this.onChangeUpdatePost}
            />
          </Form.Group>

          <Button type="submit">Edit Post</Button>
        </Form>
      </div>
    );
  }
}
