import Axios from "axios";
import React from "react";
import EditPost from "./Edit-post-page";
import svg from "../svg-sources";
import { TimelineLite } from "gsap/all";
import { ToastContainer, toast } from "react-toastify";
import axios from "axios";
import { isError } from "@hapi/joi";

export default class Post extends React.Component {
  constructor(props) {
    super(props);
    this.cards = [];
    this.tl = new TimelineLite({ paused: true });

    this.state = {
      vote: this.props.posts.vote,
      alert: "",
    };
  }

  componentDidMount() {
    this.tl.staggerTo(this.cards, 1, { autoAlpha: 1, y: 10 }, 1).play();
  }

  CommentTriggerButton = () => {
    return (
      <>
        <div
          className="col-md-auto p-0"
          type="button"
          data-toggle="collapse"
          data-target={`#id${this.props.posts._id}`}
          aria-expanded="false"
          aria-controls="comment-form"
          id="comment-button"
        >
          {svg.comment}
        </div>
      </>
    );
  };

  deletePost(post_id) {
    axios
      .delete(`${this.props.server}/deletepost/${post_id}`)
      .then((res) => {
        let isError = res.data.error;
        isError
          ? this.notify(isError, isError)
          : this.notify(isError, "Post Deleted");
      })
      .catch((err) => console.log(err));
  }

  notify = (isError, alert) => {
    if (isError) {
      toast.error(`${alert}`, {
        position: "top-center",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
    } else {
      toast.success(`${alert}`, {
        position: "top-center",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
    }
  };

  EditDeletePostOption = () => {
    return (
      <div className="dropdown float-right" id="edit-btn">
        {svg.options}

        <div
          className="dropdown-menu dropdown-menu-right"
          aria-labelledby="dropdownMenuButton"
        >
          <EditPost
            postID={this.props.posts._id}
            server={this.props.server}
            className="dropdown-item"
            notify={this.notify}
          />
          <button
            className="dropdown-item"
            onClick={() => {
              this.deletePost(this.props.posts.post._id);
            }}
          >
            Delete
          </button>
        </div>
      </div>
    );
  };

  CommentPostSection = () => {
    return (
      <>
        <div className="px-1 mb-3 ml-3">{this.props.replyTab}</div>
        <div id="divider"></div>
        <div>
          {this.props.posts.reply.map((replyObj) => {
            return (
              <div className="comment-box px-3" key={Math.random()}>
                <div className="card-body">
                  <div className="media">
                    <img
                      src={this.props.picture()}
                      className="mr-3"
                      alt="..."
                      width="80"
                      height="80"
                    />
                    <div className="media-body">
                      <h6 className="card-title mb-0 pb-0">
                        {replyObj.userName} replied:
                      </h6>
                      <p className="text-sm-left mb-1" id="date">
                        posted: {replyObj.postedDate}
                      </p>
                      <p className="text-left reply-content p-3 mb-0">
                        {replyObj.replyText}
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </>
    );
  };

  upvote = (post_id) => {
    Axios.post(`${this.props.server}/upvotepost/${post_id}`)
      .then((res) => {
        this.setState({ vote: this.state.vote + 1 });
        console.log(res);
      })
      .catch((err) => console.log(err));
  };

  downvote = (post_id) => {
    Axios.post(`${this.props.server}/downvotepost/${post_id}`)
      .then((res) => {
        this.setState({ vote: this.state.vote - 1 });
        console.log(res);
      })
      .catch((err) => console.log(err));
  };

  UpvoteDownvoteSection = () => {
    return (
      <>
        <div
          className="col w-auto d-flex justify-content-end"
          id="upvote-downvote"
        >
          <div className="px-2">
            <svg
              type="button"
              onClick={() => this.upvote(this.props.posts._id)}
              width="24"
              height="24"
              viewBox="0 0 1000 1000"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M 100 0C 45 0 0 45 0 100C 0 100 0 900 0 900C 0 955 45 1000 100 1000C 100 1000 900 1000 900 1000C 955 1000 1000 955 1000 900C 1000 900 1000 100 1000 100C 1000 45 955 0 900 0C 900 0 100 0 100 0C 100 0 100 0 100 0M 100 75C 100 75 900 75 900 75C 914 75 925 86 925 100C 925 100 925 900 925 900C 925 914 914 925 900 925C 900 925 100 925 100 925C 86 925 75 914 75 900C 75 900 75 100 75 100C 75 86 86 75 100 75C 100 75 100 75 100 75 M 526 223C 526 223 526 223 526 223C 526 223 526 223 527 223C 527 223 727 423 727 423C 742 438 742 462 727 477C 712 492 688 492 673 477C 673 477 538 341 538 341C 538 341 538 750 538 750C 538 771 522 788 501 788C 480 789 463 771 463 750C 463 750 463 340 463 340C 463 340 327 477 327 477C 312 492 288 492 273 477C 258 462 258 438 273 423C 273 423 469 228 469 228C 476 218 488 212 500 212C 509 212 519 216 526 223C 526 223 526 223 526 223" />
            </svg>
          </div>
          <div className="px-2" id="vote-result">
            {this.state.vote}
          </div>
          <div className="px-2">
            <svg
              type="button"
              onClick={() => this.downvote(this.props.posts._id)}
              width="24"
              height="24"
              viewBox="0 0 1000 1000"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                d="M 100 0C 45 0 0 45 0 100C 0 100 0 900 0 900C 0 955 45 1000 100 1000C 100 1000 900 1000 900 1000C 955 1000 1000 955 1000 900C 1000 900 1000 100 1000 100C 1000 45 955 0 900 0C 900 0 100 0 100 0C 100 0 100 0 100 0M 100 75C 100 75 900 75 900 75C 914 75 925 86 925 100C 925 100 925 900 925 900C 925 914 914 925 900 925C 900 925 100 925 100 925C 86 925 75 914 75 900C 75 900 75 100 75 100C 75 86 86 75 100 75C 100 75 100 75 100 75M 526 223C 526 223 526 223 526 223C 526 223 526 223 527 223C 527 223 727 423 727 423C 742 438 742 462 727 477C 712 492 688 492 673 477C 673 477 538 341 538 341C 538 341 538 750 538 750C 538 771 522 788 501 788C 480 789 463 771 463 750C 463 750 463 340 463 340C 463 340 327 477 327 477C 312 492 288 492 273 477C 258 462 258 438 273 423C 273 423 469 228 469 228C 476 218 488 212 500 212C 509 212 519 216 526 223C 526 223 526 223 526 223"
                transform="rotate(180,500,500)"
              />
            </svg>
          </div>
        </div>
      </>
    );
  };

  MainPost = () => {
    return (
      <div className="post-box">
        <br></br>
        <div className="card w-75 mx-auto" id="post-box">
          <div className="card-body mb-1 pb-1 border-bottom" id="post-cont">
            <div className="media" id="post-box">
              <img
                src={this.props.picture()}
                height="140"
                width="140"
                className="mr-3"
                alt="..."
              />
              <div className="media-body">
                <div className="container h-auto pb-0 mx-0 px-0">
                  <div className="h-auto">
                    <div className="float-right">
                      {this.EditDeletePostOption()}
                    </div>
                    <h5 className="card-title h-auto mb-0">
                      {this.props.posts.userName}
                    </h5>
                  </div>
                </div>
                <p className="text-sm-left mb-1" id="date">
                  posted: {this.props.posts.dateCreated}
                </p>
                <p className="text-left post-content p-3">
                  {this.props.posts.post}
                </p>
                <div className="container mb-3">
                  <div className="row" id="replyGroup">
                    {this.CommentTriggerButton()}
                    {this.UpvoteDownvoteSection()}
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="collapse px-5  mt-2" id={`id${this.props.posts._id}`}>
            {this.CommentPostSection()}
          </div>
          <div className=""></div>
        </div>
      </div>
    );
  };

  render() {
    return (
      <div>
        <this.MainPost />
      </div>
    );
  }
}
